int lfs_file_opencfg(lfs_t *lfs, lfs_file_t *file,
        const char *path, int flags,
        const struct lfs_file_config *cfg) {
    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        return err;
    }

    LFS_TRACE("lfs_file_opencfg(%p, %p, \"%s\", %x, %p {"
                 ".buffer=%p, .attrs=%p, .attr_count=%"PRIu32"})",
            (void*)lfs, (void*)file, path, (unsigned)flags,
            (void*)cfg, cfg->buffer, (void*)cfg->attrs, cfg->attr_count);

    /* Open a persistent log for this test/diagnostic. */
    FILE *log = fopen("/tmp/logs/855.txt", "a");
    if (log) {
        fprintf(log, "ENTER lfs_file_opencfg: lfs=%p file=%p path=\"%s\" flags=0x%x cfg=%p\n",
                (void*)lfs, (void*)file, path ? path : "(null)", flags, (void*)cfg);
        fprintf(log, " cfg->buffer=%p cfg->attrs=%p cfg->attr_count=%" PRIu32 "\n",
                cfg->buffer, (void*)cfg->attrs, cfg->attr_count);
        fflush(log);
    }

    /* Probe: record the cast to a non-const target type that is the focus of the
       reported MISRA Rule 8.13 violation. We call lfs_mlist_isopen once and
       log its return so other agents can reproduce the control flow and state. */
    if (log) {
        fprintf(log, "About to call lfs_mlist_isopen with mlist=%p file_cast=%p (cast to non-const struct lfs_mlist *)\n",
                (void*)lfs->mlist, (void*)((struct lfs_mlist*)file));
        fflush(log);
    }

    int isopen = lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file);

    if (log) {
        fprintf(log, "lfs_mlist_isopen returned %d\n", isopen);
        fflush(log);
    }

    LFS_ASSERT(!isopen);

    err = lfs_file_opencfg_(lfs, file, path, flags, cfg);

    if (log) {
        fprintf(log, "lfs_file_opencfg_ -> %d\n", err);
        fclose(log);
    }

    LFS_TRACE("lfs_file_opencfg -> %d", err);
    LFS_UNLOCK(lfs->cfg);
    return err;
}
