int lfs_file_rewind(lfs_t *lfs, lfs_file_t *file) {
    /* Probe log file to record control flow and state for debugging the invariant lock scenario */
    FILE *probe = fopen("/tmp/logs/729.txt", "a");

    int err = LFS_LOCK(lfs->cfg);

    if (probe) {
        fprintf(probe, "ENTER lfs_file_rewind: lfs=%p file=%p LFS_LOCK returned %d\n",
                (void*)lfs, (void*)file, err);
        fflush(probe);
    }

    if (err) {
        if (probe) {
            fprintf(probe, "lfs_file_rewind: lock failed (err=%d), returning early\n", err);
            fclose(probe);
        }
        return err;
    }

    if (probe) {
        fprintf(probe, "lfs_file_rewind: lock succeeded (err=%d), proceeding to lfs_file_rewind_\n", err);
        fflush(probe);
    }

    LFS_TRACE("lfs_file_rewind(%p, %p)", (void*)lfs, (void*)file);

    err = lfs_file_rewind_(lfs, file);

    if (probe) {
        fprintf(probe, "lfs_file_rewind: lfs_file_rewind_ returned %d\n", err);
        fflush(probe);
    }

    LFS_TRACE("lfs_file_rewind -> %d", err);

    LFS_UNLOCK(lfs->cfg);

    if (probe) {
        fprintf(probe, "EXIT lfs_file_rewind: returning %d\n", err);
        fclose(probe);
    }

    return err;
}
