int lfs_file_rewind(lfs_t *lfs, lfs_file_t *file) {
    /* Probe file for logging control flow and state to help reproduce unreachable branch */
    FILE *probe = fopen("/tmp/logs/726.txt", "a");

    int err = LFS_LOCK(lfs->cfg);

    if (probe) {
        fprintf(probe, "lfs_file_rewind ENTER: lfs=%p file=%p\n", (void*)lfs, (void*)file);
        fprintf(probe, "After LFS_LOCK: err=%d\n", err);
        fflush(probe);
    }

    if (err) {
        /* This branch is expected to be unreachable when LFS_LOCK expands to ((void)cfg,0). */
        if (probe) {
            fprintf(probe, "Branch if(err) taken: returning err=%d\n", err);
            fclose(probe);
        }
        return err;
    }

    if (probe) {
        fprintf(probe, "Branch if(err) not taken: calling lfs_file_rewind_()\n");
        fflush(probe);
    }

    LFS_TRACE("lfs_file_rewind(%p, %p)", (void*)lfs, (void*)file);

    err = lfs_file_rewind_(lfs, file);

    LFS_TRACE("lfs_file_rewind -> %d", err);

    if (probe) {
        fprintf(probe, "lfs_file_rewind_ returned: %d\n", err);
    }

    LFS_UNLOCK(lfs->cfg);

    if (probe) {
        fprintf(probe, "After LFS_UNLOCK: returning %d\n", err);
        fclose(probe);
    }

    return err;
}
