int lfs_file_rewind(lfs_t *lfs, lfs_file_t *file) {
    int err = LFS_LOCK(lfs->cfg);

    /* Open a light-weight append-only log so external tests can inspect control flow/state. */
    FILE *___lfs_log = fopen("/tmp/logs/728.txt", "a");
    if (___lfs_log) {
        fprintf(___lfs_log, "lfs_file_rewind: ENTRY lfs=%p file=%p cfg=%p LFS_LOCK_result=%d\n",
                (void*)lfs, (void*)file, (void*)(lfs ? lfs->cfg : NULL), err);
        fflush(___lfs_log);
    }

    if (err) {
        if (___lfs_log) {
            fprintf(___lfs_log, "lfs_file_rewind: early return due to LFS_LOCK -> err=%d\n", err);
            fclose(___lfs_log);
        }
        return err;
    }

    LFS_TRACE("lfs_file_rewind(%p, %p)", (void*)lfs, (void*)file);

    if (___lfs_log) {
        fprintf(___lfs_log, "lfs_file_rewind: calling lfs_file_rewind_ (err before call=%d)\n", err);
        fflush(___lfs_log);
    }

    err = lfs_file_rewind_(lfs, file);

    if (___lfs_log) {
        fprintf(___lfs_log, "lfs_file_rewind: returned from lfs_file_rewind_ -> err=%d\n", err);
        fflush(___lfs_log);
    }

    LFS_TRACE("lfs_file_rewind -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    if (___lfs_log) {
        fprintf(___lfs_log, "lfs_file_rewind: LFS_UNLOCK called, final return err=%d\n", err);
        fclose(___lfs_log);
    }

    return err;
}
