int lfs_file_rewind(lfs_t *lfs, lfs_file_t *file) {
    /* Preserve original locking behavior */
    int err = LFS_LOCK(lfs->cfg);

    /* Capture cfg pointer safely for logging (avoid deref if lfs == NULL) */
    void *cfg_ptr = (void*)(lfs ? (void*)lfs->cfg : (void*)NULL);

    /* Log entry and lock result */
    {
        FILE *log = fopen("/tmp/logs/728.txt", "a");
        if (log) {
            fprintf(log, "ENTER: lfs_file_rewind called\n");
            fprintf(log, "  lfs=%p\n", (void*)lfs);
            fprintf(log, "  file=%p\n", (void*)file);
            fprintf(log, "  cfg=%p\n", cfg_ptr);
            fprintf(log, "  after LFS_LOCK -> %d\n", err);
            fclose(log);
        }
    }

    if (err) {
        /* Early return is part of original behavior; log it for evidence */
        FILE *log = fopen("/tmp/logs/728.txt", "a");
        if (log) {
            fprintf(log, "EXIT: lfs_file_rewind early return err=%d\n", err);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_file_rewind(%p, %p)", (void*)lfs, (void*)file);

    err = lfs_file_rewind_(lfs, file);

    /* Log result of the internal rewind */
    {
        FILE *log = fopen("/tmp/logs/728.txt", "a");
        if (log) {
            fprintf(log, "INTERNAL: lfs_file_rewind_ returned %d\n", err);
            fclose(log);
        }
    }

    LFS_TRACE("lfs_file_rewind -> %d", err);

    /* Log just before and just after calling LFS_UNLOCK to show it's invoked
       (the macro may be a no-op in some builds). This records the control path
       and the cfg pointer so reviewers can see that LFS_UNLOCK was executed
       but may have had no observable effect. */
    {
        FILE *log = fopen("/tmp/logs/728.txt", "a");
        if (log) {
            fprintf(log, "BEFORE_UNLOCK: calling LFS_UNLOCK(cfg=%p)\n", cfg_ptr);
            fclose(log);
        }
    }

    LFS_UNLOCK(lfs->cfg);

    {
        FILE *log = fopen("/tmp/logs/728.txt", "a");
        if (log) {
            fprintf(log, "AFTER_UNLOCK: returned from LFS_UNLOCK (cfg=%p)\n", cfg_ptr);
            fprintf(log, "EXIT: lfs_file_rewind final return=%d\n", err);
            fclose(log);
        }
    }

    return err;
}
