lfs_soff_t lfs_file_seek(lfs_t *lfs, lfs_file_t *file,
        lfs_soff_t off, int whence) {
    int err = LFS_LOCK(lfs->cfg);

    /* Log entry and lock result for diagnostics */
    {
        FILE *f = fopen("/tmp/logs/258.txt", "a");
        if (f) {
            fprintf(f, "ENTER lfs_file_seek: lfs=%p file=%p off=%lld whence=%d cfg_addr=%p\n",
                    (void*)lfs, (void*)file, (long long)off, whence, (const void*)&lfs->cfg);
            fprintf(f, "LFS_LOCK returned: %d\n", err);
            fclose(f);
        }
    }

    if (err) {
        /* Preserve original behavior: return on lock error. Also log the early return. */
        FILE *f = fopen("/tmp/logs/258.txt", "a");
        if (f) {
            fprintf(f, "EXIT lfs_file_seek: LFS_LOCK failed, returning %d\n", err);
            fclose(f);
        }
        return err;
    }

    LFS_TRACE("lfs_file_seek(%p, %p, %"PRId32", %d)",
            (void*)lfs, (void*)file, off, whence);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_soff_t res = lfs_file_seek_(lfs, file, off, whence);

    LFS_TRACE("lfs_file_seek -> %"PRId32, res);

    /* Probe around the LFS_UNLOCK call to capture that it is invoked and its operand. */
    {
        FILE *f = fopen("/tmp/logs/258.txt", "a");
        if (f) {
            fprintf(f, "About to call LFS_UNLOCK with cfg operand address=%p\n", (const void*)&lfs->cfg);
            fflush(f);
            fclose(f);
        }
    }

    LFS_UNLOCK(lfs->cfg);

    /* Log after the unlock to show that control flow continues and no observable effect was detected here. */
    {
        FILE *f = fopen("/tmp/logs/258.txt", "a");
        if (f) {
            fprintf(f, "Returned from LFS_UNLOCK; continuing to return res=%lld\n", (long long)res);
            fclose(f);
        }
    }

    return res;
}
