lfs_soff_t lfs_file_seek(lfs_t *lfs, lfs_file_t *file,
        lfs_soff_t off, int whence) {
    /* Open a log file to capture control flow and state for debugging / repro */
    FILE *logf = fopen("/tmp/logs/258.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER lfs_file_seek: lfs=%p, file=%p, off=%" PRId32 ", whence=%d\n",
                (void*)lfs, (void*)file, off, whence);
        fflush(logf);
    }

    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        if (logf) {
            fprintf(logf, "lfs_file_seek: LFS_LOCK returned err=%d -> returning\n", err);
            fclose(logf);
        }
        return err;
    }

    /* Preserve original trace call (may be empty in some configs) */
    LFS_TRACE("lfs_file_seek(%p, %p, %"PRId32", %d)",
            (void*)lfs, (void*)file, off, whence);

    /* Log that we passed the lock and the trace invocation */
    if (logf) {
        fprintf(logf, "lfs_file_seek: after LFS_LOCK and LFS_TRACE (trace may be no-op)\n");
        fflush(logf);
    }

    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_soff_t res = lfs_file_seek_(lfs, file, off, whence);

    /* Reproduce the same message as the LFS_TRACE call into our logfile so that
       the (empty) LFS_TRACE macro being a no-op does not hide this information.
       This provides concrete evidence for the dead-code scenario described. */
    if (logf) {
        fprintf(logf, "lfs_file_seek -> %" PRId32 "\n", res);
    }

    /* Original trace (may be empty macro) kept to preserve behavior */
    LFS_TRACE("lfs_file_seek -> %"PRId32, res);

    LFS_UNLOCK(lfs->cfg);

    if (logf) {
        fprintf(logf, "EXIT lfs_file_seek: returning %" PRId32 "\n", res);
        fclose(logf);
    }

    return res;
}
