lfs_soff_t lfs_file_seek(lfs_t *lfs, lfs_file_t *file,
        lfs_soff_t off, int whence) {
    /* Open a persistent log for probes. Keep append mode so multiple
       calls accumulate evidence. If fopen is not available in the
       build, this will be NULL and logging will be skipped. */
    FILE *probe_log = fopen("/tmp/logs/258.txt", "a");

    int err = LFS_LOCK(lfs->cfg);
    if (probe_log) {
        fprintf(probe_log, "ENTER lfs_file_seek: lfs=%p file=%p off=%" PRId32 " whence=%d\n",
                (void*)lfs, (void*)file, off, whence);
        fprintf(probe_log, "After LFS_LOCK: err=%d\n", err);
        fflush(probe_log);
    }

    if (err) {
        if (probe_log) {
            fprintf(probe_log, "EXIT lfs_file_seek (early return): err=%d\n", err);
            fclose(probe_log);
        }
        return err;
    }

    LFS_TRACE("lfs_file_seek(%p, %p, %"PRId32", %d)",
            (void*)lfs, (void*)file, off, whence);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    if (probe_log) {
        fprintf(probe_log, "Calling lfs_file_seek_: lfs=%p file=%p\n", (void*)lfs, (void*)file);
        fflush(probe_log);
    }

    lfs_soff_t res = lfs_file_seek_(lfs, file, off, whence);

    if (probe_log) {
        fprintf(probe_log, "Returned from lfs_file_seek_: res=%" PRId32 "\n", res);
        fflush(probe_log);
    }

    LFS_TRACE("lfs_file_seek -> %"PRId32, res);

    /* Probe around the unlock call to capture the cfg pointer and to
       show that LFS_UNLOCK is invoked. In some builds LFS_UNLOCK may be
       defined as ((void)cfg) and thus be a no-op; these logs provide
       evidence (call was made but no observable state change logged).
       We do not alter the behaviour of LFS_UNLOCK. */
    if (probe_log) {
        fprintf(probe_log, "Before LFS_UNLOCK: cfg=%p\n", (void*)lfs->cfg);
        fflush(probe_log);
    }

    LFS_UNLOCK(lfs->cfg);

    if (probe_log) {
        /* Capture post-unlock state visible here (pointer values).
           This demonstrates that the call occurred. */
        fprintf(probe_log, "After LFS_UNLOCK: cfg=%p\n", (void*)lfs->cfg);
        fprintf(probe_log, "EXIT lfs_file_seek: returning res=%" PRId32 "\n", res);
        fclose(probe_log);
    }

    return res;
}
