static lfs_soff_t lfs_file_seek_(lfs_t *lfs, lfs_file_t *file,
        lfs_soff_t off, int whence) {
    FILE *log = fopen("/tmp/logs/135.txt", "a");
    if (log) {
        fprintf(log, "ENTER lfs_file_seek_: lfs=%p file=%p off=%lld whence=%d file->pos=%lld\n",
                (void *)lfs, (void *)file, (long long)off, whence,
                (long long)(file ? file->pos : 0));
    }

    // find new pos
    //
    // fortunately for us, littlefs is limited to 31-bit file sizes, so we
    // don't have to worry too much about integer overflow
    lfs_off_t npos = file->pos;
    if (whence == LFS_SEEK_SET) {
        npos = off;
        if (log) fprintf(log, "whence=SEEK_SET -> npos=%lld\n", (long long)npos);
    } else if (whence == LFS_SEEK_CUR) {
        npos = file->pos + (lfs_off_t)off;
        if (log) fprintf(log, "whence=SEEK_CUR -> npos=%lld (file->pos=%lld + off=%lld)\n",
                        (long long)npos, (long long)file->pos, (long long)off);
    } else if (whence == LFS_SEEK_END) {
        npos = (lfs_off_t)lfs_file_size_(lfs, file) + (lfs_off_t)off;
        if (log) fprintf(log, "whence=SEEK_END -> npos=%lld (filesize+off)\n", (long long)npos);
    } else {
        if (log) fprintf(log, "whence=UNKNOWN(%d) -> using default npos=%lld\n", whence, (long long)npos);
    }

    if (npos > lfs->file_max) {
        // file position out of range
        if (log) {
            fprintf(log, "ERROR: npos (%lld) > file_max (%lld) -> return LFS_ERR_INVAL\n",
                    (long long)npos, (long long)lfs->file_max);
            fclose(log);
        }
        return LFS_ERR_INVAL;
    }

    if (file->pos == npos) {
        // noop - position has not changed
        if (log) {
            fprintf(log, "NOOP: file->pos == npos (%lld) -> return npos\n", (long long)npos);
            fclose(log);
        }
        return npos;
    }

    // if we're only reading and our new offset is still in the file's cache
    // we can avoid flushing and needing to reread the data
    if ((file->flags & LFS_F_READING)
            && file->off != lfs->cfg->block_size) {
        if (log) fprintf(log, "READING path: file->flags=0x%08x file->off=%lld block_size=%lld\n",
                        (unsigned)file->flags, (long long)file->off, (long long)lfs->cfg->block_size);

        /*
         * The original code passes the address of a non-const compound literal
         * here: &(lfs_off_t){file->pos}
         * Keep that usage to reproduce the reported scenario. We capture the
         * pointer to the compound literal so we can log it and then pass it
         * unchanged to lfs_ctz_index. The compound literal has automatic
         * storage duration until the end of this enclosing block.
         */
        lfs_off_t *otemp = &(lfs_off_t){file->pos};
        if (log) {
            fprintf(log, "Calling lfs_ctz_index for old pos: value=%lld ptr=%p\n",
                    (long long)*otemp, (void *)otemp);
        }
        int oindex = lfs_ctz_index(lfs, otemp);
        if (log) fprintf(log, "lfs_ctz_index returned oindex=%d\n", oindex);

        lfs_off_t noff = npos;
        if (log) fprintf(log, "Preparing new offset: noff=%lld ptr=%p\n", (long long)noff, (void *)&noff);
        int nindex = lfs_ctz_index(lfs, &noff);
        if (log) fprintf(log, "lfs_ctz_index returned nindex=%d noff(after)=%lld\n", nindex, (long long)noff);

        if (oindex == nindex
                && noff >= file->cache.off
                && noff < file->cache.off + file->cache.size) {
            file->pos = npos;
            file->off = noff;
            if (log) {
                fprintf(log, "CACHE HIT: updated file->pos=%lld file->off=%lld -> return npos=%lld\n",
                        (long long)file->pos, (long long)file->off, (long long)npos);
                fclose(log);
            }
            return npos;
        }
        if (log) fprintf(log, "CACHE MISS or not in range: oindex=%d nindex=%d noff=%lld cache.off=%lld cache.size=%lld\n",
                        oindex, nindex, (long long)noff,
                        (long long)file->cache.off, (long long)file->cache.size);
    }

    // write out everything beforehand, may be noop if rdonly
    if (log) fprintf(log, "Flushing file before seek\n");
    int err = lfs_file_flush(lfs, file);
    if (err) {
        if (log) {
            fprintf(log, "lfs_file_flush returned err=%d -> returning err\n", err);
            fclose(log);
        }
        return err;
    }

    // update pos
    file->pos = npos;
    if (log) {
        fprintf(log, "UPDATED: file->pos set to %lld -> returning %lld\n", (long long)file->pos, (long long)npos);
        fclose(log);
    }
    return npos;
}
