lfs_soff_t lfs_file_size(lfs_t *lfs, lfs_file_t *file) {
    /* Local include to ensure FILE and fopen/fprintf are available when
       this function is injected into the existing translation unit. Placing
       the include here keeps this definition self-contained as requested. */
    #include <stdio.h>

    FILE *log = fopen("/tmp/logs/476.txt", "a");
    if (log) {
        fprintf(log, "ENTER lfs_file_size: lfs=%p file=%p\n", (void*)lfs, (void*)file);
    }

    int err = LFS_LOCK(lfs->cfg);
    if (log) {
        fprintf(log, "after LFS_LOCK: err=%d\n", err);
    }

    if (err) {
        if (log) {
            fprintf(log, "early return due to err != 0: returning %d\n", err);
            fflush(log);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_file_size(%p, %p)", (void*)lfs, (void*)file);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_soff_t res = lfs_file_size_(lfs, file);

    if (log) {
        fprintf(log, "after lfs_file_size_: res=%lld\n", (long long)res);
    }

    LFS_TRACE("lfs_file_size -> %"PRIu32, res);
    LFS_UNLOCK(lfs->cfg);

    if (log) {
        fprintf(log, "exiting lfs_file_size: returning res=%lld\n", (long long)res);
        fflush(log);
        fclose(log);
    }

    return res;
}
