lfs_soff_t lfs_file_size(lfs_t *lfs, lfs_file_t *file) {
    /* Probe log to capture control flow and state for Rule 2.2 investigation. */
    FILE *___lfs_probe_f = fopen("/tmp/logs/478.txt", "a");
    if (___lfs_probe_f) {
        fprintf(___lfs_probe_f, "ENTER lfs_file_size: lfs=%p file=%p\n", (void*)lfs, (void*)file);
        /* Log the raw value of lfs->cfg to capture the sub-expression observed in the LFS_LOCK macro. */
        /* Cast to void* for printing; this mirrors the member access that is argued to be a dead no-op. */
        fprintf(___lfs_probe_f, "pre-lock: lfs->cfg=%p\n", (void*)lfs->cfg);
        fclose(___lfs_probe_f);
    }

    int err = LFS_LOCK(lfs->cfg);

    ___lfs_probe_f = fopen("/tmp/logs/478.txt", "a");
    if (___lfs_probe_f) {
        fprintf(___lfs_probe_f, "after LFS_LOCK: err=%d lfs=%p\n", err, (void*)lfs);
        fprintf(___lfs_probe_f, "(re-read) lfs->cfg=%p\n", (void*)lfs->cfg);
        fclose(___lfs_probe_f);
    }

    if (err) {
        /* Log the early return so callers/tests can see that this path was taken. */
        ___lfs_probe_f = fopen("/tmp/logs/478.txt", "a");
        if (___lfs_probe_f) {
            fprintf(___lfs_probe_f, "lfs_file_size: returning err=%d (early)\n", err);
            fclose(___lfs_probe_f);
        }
        return err;
    }

    LFS_TRACE("lfs_file_size(%p, %p)", (void*)lfs, (void*)file);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_soff_t res = lfs_file_size_(lfs, file);

    ___lfs_probe_f = fopen("/tmp/logs/478.txt", "a");
    if (___lfs_probe_f) {
        fprintf(___lfs_probe_f, "lfs_file_size_: res=%" PRIu32 "\n", (uint32_t)res);
        fprintf(___lfs_probe_f, "about to call LFS_UNLOCK with lfs->cfg=%p\n", (void*)lfs->cfg);
        fclose(___lfs_probe_f);
    }

    LFS_TRACE("lfs_file_size -> %"PRIu32, res);
    LFS_UNLOCK(lfs->cfg);

    ___lfs_probe_f = fopen("/tmp/logs/478.txt", "a");
    if (___lfs_probe_f) {
        fprintf(___lfs_probe_f, "EXIT lfs_file_size: returning res=%" PRIu32 "\n", (uint32_t)res);
        fclose(___lfs_probe_f);
    }

    return res;
}
