lfs_soff_t lfs_file_size(lfs_t *lfs, lfs_file_t *file) {
    int err = LFS_LOCK(lfs->cfg);
    /* Probe: log entry and LFS_LOCK result so we can observe control flow even when LFS_TRACE is empty. */
    {
        FILE *__logf = fopen("/tmp/logs/478.txt", "a");
        if (__logf) {
            fprintf(__logf, "lfs_file_size: ENTRY lfs=%p file=%p LFS_LOCK_ret=%d\n", (void*)lfs, (void*)file, err);
            fclose(__logf);
        }
    }

    if (err) {
        /* Log early return due to lock error. */
        FILE *__logf = fopen("/tmp/logs/478.txt", "a");
        if (__logf) {
            fprintf(__logf, "lfs_file_size: RETURN early due to LFS_LOCK error=%d\n", err);
            fclose(__logf);
        }
        return err;
    }

    /* Original trace call (may be a null statement if LFS_TRACE is defined empty). */
    LFS_TRACE("lfs_file_size(%p, %p)", (void*)lfs, (void*)file);

    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_soff_t res = lfs_file_size_(lfs, file);

    /* Original trace of result (kept intact). */
    LFS_TRACE("lfs_file_size -> %"PRIu32, res);
    LFS_UNLOCK(lfs->cfg);

    /* Probe: log exit and result so we can observe that the LFS_TRACE calls did not produce file output in this build/config. */
    {
        FILE *__logf = fopen("/tmp/logs/478.txt", "a");
        if (__logf) {
            fprintf(__logf, "lfs_file_size: EXIT res=%lld lfs=%p file=%p\n", (long long)res, (void*)lfs, (void*)file);
            fclose(__logf);
        }
    }

    return res;
}
