lfs_soff_t lfs_file_size(lfs_t *lfs, lfs_file_t *file) {
    /* Probe logging to capture control flow and state for Rule 2.2 investigation. */
    int err = LFS_LOCK(lfs->cfg);

    /* Log entry and the immediate result of LFS_LOCK so agents can observe that
       LFS_LOCK(cfg) yields 0 in the reported configuration. */
    {
        FILE *log = fopen("/tmp/logs/478.txt", "a");
        if (log) {
            fprintf(log, "ENTER lfs_file_size: lfs=%p file=%p cfg=%p err=%d\n",
                    (void*)lfs, (void*)file, (void*)lfs->cfg, err);
            fclose(log);
        }
    }

    if (err) {
        /* If this branch were ever taken, log that occurrence. In the reported
           scenario this branch should never be taken because LFS_LOCK is
           defined as ((void)cfg,0) and thus err==0. */
        FILE *log = fopen("/tmp/logs/478.txt", "a");
        if (log) {
            fprintf(log, "BRANCH: err!=0 taken in lfs_file_size, returning %d\n", err);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_file_size(%p, %p)", (void*)lfs, (void*)file);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_soff_t res = lfs_file_size_(lfs, file);

    /* Log the result and the fact that we will unlock and return the size. */
    {
        FILE *log = fopen("/tmp/logs/478.txt", "a");
        if (log) {
            fprintf(log, "EXIT lfs_file_size: res=%lld (returned as lfs_soff_t)\n", (long long)res);
            fclose(log);
        }
    }

    LFS_TRACE("lfs_file_size -> %"PRIu32, res);
    LFS_UNLOCK(lfs->cfg);
    return res;
}
