lfs_soff_t lfs_file_size(lfs_t *lfs, lfs_file_t *file) {
    int err = LFS_LOCK(lfs->cfg);

    /* Open log file for appending; best-effort logging. */
    FILE *log = fopen("/tmp/logs/475.txt", "a");
    if (log) {
        fprintf(log, "ENTER lfs_file_size: lfs=%p, file=%p, lock_err=%d\n",
                (void*)lfs, (void*)file, err);
        fflush(log);
        fclose(log);
    }

    if (err) {
        /* Log the early return due to lock failure */
        log = fopen("/tmp/logs/475.txt", "a");
        if (log) {
            fprintf(log, "EXIT lfs_file_size (lock failed): returning err=%d, lfs=%p, file=%p\n",
                    err, (void*)lfs, (void*)file);
            fflush(log);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_file_size(%p, %p)", (void*)lfs, (void*)file);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    /* Log just before calling the underlying implementation */
    log = fopen("/tmp/logs/475.txt", "a");
    if (log) {
        fprintf(log, "CALL lfs_file_size_: lfs=%p, file=%p\n", (void*)lfs, (void*)file);
        fflush(log);
        fclose(log);
    }

    lfs_soff_t res = lfs_file_size_(lfs, file);

    /* Log the result from the underlying call */
    log = fopen("/tmp/logs/475.txt", "a");
    if (log) {
        /* Use a wide-enough integer cast to print the offset value */
        fprintf(log, "RETURNED from lfs_file_size_: res=%lld, lfs=%p, file=%p\n",
                (long long)res, (void*)lfs, (void*)file);
        fflush(log);
        fclose(log);
    }

    LFS_TRACE("lfs_file_size -> %"PRIu32, res);
    LFS_UNLOCK(lfs->cfg);
    return res;
}
