static lfs_soff_t lfs_file_size_(lfs_t *lfs, lfs_file_t *file) {
    (void)lfs;

    /* Logging: try to create log directory and append to the log file.  */
    #include <stdio.h>
    #include <sys/stat.h>
    #include <errno.h>

    /* Attempt to create the log directory; ignore error if it already exists. */
    (void)mkdir("/tmp/logs", 0755);

    FILE *__lfs_log = fopen("/tmp/logs/225.txt", "a");
    if (__lfs_log == NULL) {
        /* Fallback if the logs directory doesn't exist or can't be written. */
        __lfs_log = fopen("/tmp/225.txt", "a");
    }

    if (__lfs_log) {
        fprintf(__lfs_log, "ENTER lfs_file_size_: lfs=%p file=%p\n", (void *)lfs, (void *)file);
        if (file) {
            /* Print state observed by this function that is relevant to the violation scenario. */
            fprintf(__lfs_log,
                    "OBSERVE: file->flags=0x%08x file->pos=%lld file->ctz.size=%lld\n",
                    (unsigned)file->flags,
                    (long long)file->pos,
                    (long long)file->ctz.size);
        } else {
            fprintf(__lfs_log, "OBSERVE: file is NULL\n");
        }
        fflush(__lfs_log);
    }

#ifndef LFS_READONLY
    if (file->flags & LFS_F_WRITING) {
        /* Log branch taken and result. */
        lfs_soff_t __res = lfs_max(file->pos, file->ctz.size);
        if (__lfs_log) {
            fprintf(__lfs_log, "BRANCH: writing branch taken -> return %lld\n", (long long)__res);
            fclose(__lfs_log);
        }
        return __res;
    }
#endif

    /* Log the non-writing return value and return. */
    if (__lfs_log) {
        fprintf(__lfs_log, "BRANCH: readonly (or not writing) -> return %lld\n", (long long)file->ctz.size);
        fclose(__lfs_log);
    }

    return file->ctz.size;
}
