static lfs_soff_t lfs_file_size_(lfs_t *lfs, lfs_file_t *file) {
    (void)lfs;

    /* Include stdio here to ensure fopen/fprintf are declared even if the
       surrounding compilation unit doesn't already include <stdio.h>. */
    #include <stdio.h>

    /* Open log file (append). Keep logging non-fatal: if fopen fails we
       still preserve original behavior. */
    FILE *log = fopen("/tmp/logs/225.txt", "a");
    if (log) {
        (void)fprintf(log, "ENTER lfs_file_size_: lfs=%p file=%p\n", (void *)lfs, (void *)file);
        if (file) {
            (void)fprintf(log, "STATE: flags=0x%X pos=%llu ctz.size=%llu\n",
                          (unsigned)file->flags,
                          (unsigned long long)file->pos,
                          (unsigned long long)file->ctz.size);
        } else {
            (void)fprintf(log, "STATE: file is NULL\n");
        }
    }

#ifndef LFS_READONLY
    if (file->flags & LFS_F_WRITING) {
        if (log) {
            (void)fprintf(log, "BRANCH: LFS_F_WRITING set -> returning max(pos, ctz.size)\n");
        }
        lfs_soff_t ret = lfs_max(file->pos, file->ctz.size);
        if (log) {
            (void)fprintf(log, "EXIT lfs_file_size_: return=%llu\n", (unsigned long long)ret);
            (void)fclose(log);
        }
        return ret;
    }
#endif

    if (log) {
        (void)fprintf(log, "BRANCH: not writing (or LFS_READONLY) -> returning ctz.size\n");
        (void)fprintf(log, "EXIT lfs_file_size_: return=%llu\n", (unsigned long long)file->ctz.size);
        (void)fclose(log);
    }

    return file->ctz.size;
}
