int lfs_file_sync(lfs_t *lfs, lfs_file_t *file) {
    int err = LFS_LOCK(lfs->cfg);

    /* Probe: log entry and state after acquiring lock */
    FILE *logf = fopen("/tmp/logs/579.txt", "a");
    if (logf) {
        fprintf(logf, "lfs_file_sync entered: lfs=%p file=%p cfg=%p\n",
                (void*)lfs, (void*)file, (void*)lfs->cfg);
        fprintf(logf, "after LFS_LOCK: err=%d\n", err);
        fprintf(logf, "branch if(err) will be taken: %s\n", err ? "yes" : "no");
        fclose(logf);
    }

    if (err) {
        /* Probe: early return due to err */
        FILE *logf2 = fopen("/tmp/logs/579.txt", "a");
        if (logf2) {
            fprintf(logf2, "lfs_file_sync: early return with err=%d\n", err);
            fclose(logf2);
        }
        return err;
    }

    LFS_TRACE("lfs_file_sync(%p, %p)", (void*)lfs, (void*)file);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    err = lfs_file_sync_(lfs, file);

    /* Probe: log result of lfs_file_sync_ */
    FILE *logf3 = fopen("/tmp/logs/579.txt", "a");
    if (logf3) {
        fprintf(logf3, "lfs_file_sync_: returned err=%d\n", err);
        fclose(logf3);
    }

    LFS_TRACE("lfs_file_sync -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    FILE *logf4 = fopen("/tmp/logs/579.txt", "a");
    if (logf4) {
        fprintf(logf4, "lfs_file_sync: unlocked cfg=%p, returning %d\n", (void*)lfs->cfg, err);
        fclose(logf4);
    }

    return err;
}
