int lfs_file_sync(lfs_t *lfs, lfs_file_t *file) {
    int err = LFS_LOCK(lfs->cfg);

    /* Probe: log entry and result of LFS_LOCK to /tmp/logs/576.txt */
    {
        FILE *log = fopen("/tmp/logs/576.txt", "a");
        if (log) {
            fprintf(log, "lfs_file_sync ENTER: lfs=%p file=%p\n", (void*)lfs, (void*)file);
            fprintf(log, "lfs_file_sync: after LFS_LOCK -> err=%d\n", err);
            fclose(log);
        }
    }

    if (err) {
        /* Probe: record that the error branch was taken (should be unreachable if LFS_LOCK expands to 0) */
        FILE *log = fopen("/tmp/logs/576.txt", "a");
        if (log) {
            fprintf(log, "lfs_file_sync: taking if(err) branch, returning err=%d\n", err);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_file_sync(%p, %p)", (void*)lfs, (void*)file);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    err = lfs_file_sync_(lfs, file);

    /* Probe: log result of lfs_file_sync_ and that we're about to unlock */
    {
        FILE *log = fopen("/tmp/logs/576.txt", "a");
        if (log) {
            fprintf(log, "lfs_file_sync: lfs_file_sync_ returned %d\n", err);
            fprintf(log, "lfs_file_sync: calling LFS_UNLOCK(lfs->cfg)\n");
            fclose(log);
        }
    }

    LFS_TRACE("lfs_file_sync -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    /* Probe: final exit log */
    {
        FILE *log = fopen("/tmp/logs/576.txt", "a");
        if (log) {
            fprintf(log, "lfs_file_sync EXIT: returning %d\n", err);
            fclose(log);
        }
    }

    return err;
}
