int lfs_file_sync(lfs_t *lfs, lfs_file_t *file) {
    int err = LFS_LOCK(lfs->cfg);

    /* Probe log to capture entry and lock result for reachability evidence */
    FILE *log = fopen("/tmp/logs/576.txt", "a");
    if (log) {
        fprintf(log, "ENTER lfs_file_sync: lfs=%p file=%p\n", (void*)lfs, (void*)file);
        fprintf(log, "LFS_LOCK returned: %d\n", err);
        fflush(log);
        fclose(log);
    } else {
        /* If log can't be opened, still continue and surface via LFS_TRACE */
        LFS_TRACE("lfs_file_sync: could not open /tmp/logs/576.txt");
    }

    if (err) {
        /* Log the early return branch so the test can detect if this path is ever taken. */
        log = fopen("/tmp/logs/576.txt", "a");
        if (log) {
            fprintf(log, "EARLY RETURN from lfs_file_sync due to LFS_LOCK error: %d\n", err);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_file_sync(%p, %p)", (void*)lfs, (void*)file);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    err = lfs_file_sync_(lfs, file);

    /* Log the result of the internal sync and that we're about to unlock. */
    log = fopen("/tmp/logs/576.txt", "a");
    if (log) {
        fprintf(log, "lfs_file_sync_ returned: %d\n", err);
        fprintf(log, "Calling LFS_UNLOCK(lfs->cfg)\n");
        fclose(log);
    }

    LFS_TRACE("lfs_file_sync -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    /* Final log entry showing completion and returned error code. */
    log = fopen("/tmp/logs/576.txt", "a");
    if (log) {
        fprintf(log, "EXIT lfs_file_sync: return %d\n", err);
        fclose(log);
    }

    return err;
}
