int lfs_file_sync(lfs_t *lfs, lfs_file_t *file) {
    /* Preserve original locking behaviour */
    int err = LFS_LOCK(lfs->cfg);

    /* Open a log file to capture control flow and state for analysis */
    FILE *logf = fopen("/tmp/logs/578.txt", "a");
    if (logf) {
        /* Log entry and the observed values: lfs, file, cfg pointer and lock result */
        fprintf(logf, "ENTER lfs_file_sync: lfs=%p file=%p cfg=%p LFS_LOCK_result=%d\n",
                (void*)lfs, (void*)file, (void*)lfs->cfg, err);
        fflush(logf);
    }

    if (err) {
        /* If lock failed, preserve original behaviour and log the early return. */
        if (logf) {
            fprintf(logf, "lfs_file_sync: LFS_LOCK returned %d, returning early\n", err);
            fclose(logf);
        }
        return err;
    }

    /* Original tracing and assertion kept intact */
    LFS_TRACE("lfs_file_sync(%p, %p)", (void*)lfs, (void*)file);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    /* Call the underlying implementation and capture its result */
    err = lfs_file_sync_(lfs, file);

    /* Log the result of the sync operation */
    if (logf) {
        fprintf(logf, "lfs_file_sync: lfs_file_sync_ returned %d\n", err);
        fclose(logf);
    }

    LFS_TRACE("lfs_file_sync -> %d", err);
    LFS_UNLOCK(lfs->cfg);
    return err;
}
