int lfs_file_sync(lfs_t *lfs, lfs_file_t *file) {
    int err = LFS_LOCK(lfs->cfg);

    /* Open a persistent log so external tools can observe control flow/state. */
    FILE *logf = fopen("/tmp/logs/578.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER lfs_file_sync: lfs=%p file=%p lock_err=%d\n",
                (void*)lfs, (void*)file, err);
        fflush(logf);
    }

    if (err) {
        if (logf) {
            fprintf(logf, "EARLY RETURN lfs_file_sync: lock failed err=%d\n", err);
            fflush(logf);
            fclose(logf);
        }
        return err;
    }

    /* Original trace (may be a no-op macro). We explicitly log around it
       so that absence of LFS_TRACE output can be observed in /tmp/logs/578.txt. */
    LFS_TRACE("lfs_file_sync(%p, %p)", (void*)lfs, (void*)file);
    if (logf) {
        fprintf(logf, "CALLED LFS_TRACE for entry (may be no-op)\n");
        fflush(logf);
    }

    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    err = lfs_file_sync_(lfs, file);

    if (logf) {
        fprintf(logf, "AFTER lfs_file_sync_: err=%d\n", err);
        fflush(logf);
    }

    /* Original trace for exit. Keep it, but also log to our file so tests can
       detect that the LFS_TRACE macro produced no visible output while our
       explicit log does appear. */
    LFS_TRACE("lfs_file_sync -> %d", err);
    if (logf) {
        fprintf(logf, "CALLED LFS_TRACE for exit (may be no-op)\n");
        fprintf(logf, "ABOUT TO UNLOCK and RETURN err=%d\n", err);
        fflush(logf);
    }

    LFS_UNLOCK(lfs->cfg);

    if (logf) {
        fprintf(logf, "UNLOCKED lfs->cfg; final return err=%d\n", err);
        fclose(logf);
    }

    return err;
}
