int lfs_file_sync(lfs_t *lfs, lfs_file_t *file) {
    /* Ensure stdio prototypes are available for logging. Including inside the
       function keeps this definition self-contained. */
    #include <stdio.h>

    FILE *log = fopen("/tmp/logs/578.txt", "a");
    if (log) {
        fprintf(log, "ENTER lfs_file_sync: lfs=%p file=%p\n", (void*)lfs, (void*)file);
    }

    int err = LFS_LOCK(lfs->cfg);
    if (log) {
        fprintf(log, "LFS_LOCK returned: %d\n", err);
    }
    if (err) {
        if (log) {
            fprintf(log, "EXIT lfs_file_sync (lock error): %d\n", err);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_file_sync(%p, %p)", (void*)lfs, (void*)file);

    /* Log whether assertions were compiled out (NDEBUG) so we can reproduce
       and evidence the dead-code scenario where LFS_ASSERT becomes a no-op. */
    #ifdef NDEBUG
    if (log) {
        fprintf(log, "Compile-time: NDEBUG defined (assertions compiled out)\n");
    }
    #else
    if (log) {
        fprintf(log, "Compile-time: NDEBUG NOT defined (assertions active)\n");
    }
    #endif

    /* Evaluate the condition once so we can both log it and pass it to the
       assertion without calling any helper twice (preserves behavior if the
       helper had side-effects). */
    int is_open = 0;
    /* Protect the call in case lfs or file pointers are NULL; logging will
       reflect that. Still call the helper as in original code to preserve
       semantics when possible. */
    if (lfs && lfs->mlist) {
        is_open = lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file);
    } else {
        /* If lfs or lfs->mlist is NULL, record that; original code would
           have invoked lfs_mlist_isopen and likely crashed. We preserve
           original behavior by still calling it when safe above. */
        if (log) {
            fprintf(log, "Warning: lfs or lfs->mlist is NULL (lfs=%p, mlist=%p)\n",
                    (void*)lfs, lfs ? (void*)lfs->mlist : NULL);
        }
    }

    if (log) {
        fprintf(log, "lfs_mlist_isopen -> %d\n", is_open);
    }

    /* Preserve the original assertion call but use the pre-evaluated value
       to avoid double-calling the predicate (which could change behavior). */
    LFS_ASSERT(is_open);

    err = lfs_file_sync_(lfs, file);
    if (log) {
        fprintf(log, "lfs_file_sync_ returned: %d\n", err);
    }

    LFS_TRACE("lfs_file_sync -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    if (log) {
        fprintf(log, "EXIT lfs_file_sync -> %d\n", err);
        fclose(log);
    }

    return err;
}
