int lfs_file_sync(lfs_t *lfs, lfs_file_t *file) {
    /* Probe log file to record control flow and state for analysis. */
    FILE *lfs_sync_log = fopen("/tmp/logs/578.txt", "a");

    int err = LFS_LOCK(lfs->cfg);
    if (lfs_sync_log) {
        fprintf(lfs_sync_log, "lfs_file_sync ENTER: lfs=%p file=%p\n", (void*)lfs, (void*)file);
        fprintf(lfs_sync_log, "After LFS_LOCK: err=%d\n", err);
        fflush(lfs_sync_log);
    }

    if (err) {
        if (lfs_sync_log) {
            fprintf(lfs_sync_log, "Early return: lock error (%d) - returning immediately\n", err);
            fclose(lfs_sync_log);
        }
        return err;
    }

    if (lfs_sync_log) {
        fprintf(lfs_sync_log, "Lock succeeded (err==0). Proceeding to LFS_TRACE and LFS_ASSERT.\n");
        fflush(lfs_sync_log);
    }

    LFS_TRACE("lfs_file_sync(%p, %p)", (void*)lfs, (void*)file);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    err = lfs_file_sync_(lfs, file);

    if (lfs_sync_log) {
        fprintf(lfs_sync_log, "lfs_file_sync_ returned: %d\n", err);
        fflush(lfs_sync_log);
    }

    LFS_TRACE("lfs_file_sync -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    if (lfs_sync_log) {
        fprintf(lfs_sync_log, "LFS_UNLOCK called. Returning: %d\n", err);
        fclose(lfs_sync_log);
    }

    return err;
}
