int lfs_file_sync(lfs_t *lfs, lfs_file_t *file) {
    int err = LFS_LOCK(lfs->cfg);
    /* Minimal entry probe so the function being reached is always logged */
    {
        FILE *log = fopen("/tmp/logs/575.txt", "a");
        if (log) {
            fprintf(log, "ENTER lfs_file_sync: lfs=%p file=%p cfg=%p\n",
                    (void*)lfs, (void*)file, (void*)lfs->cfg);
            fclose(log);
        }
    }
    if (err) {
        /* Log lock-failure early exit */
        FILE *log = fopen("/tmp/logs/575.txt", "a");
        if (log) {
            fprintf(log, "EXIT lfs_file_sync: lock failed err=%d\n", err);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_file_sync(%p, %p)", (void*)lfs, (void*)file);

    /* Probe the mlist check and capture its result for logging. This preserves
       the original assertion semantics while allowing us to record the call
       and the cast that triggers the reported MISRA concern. */
    int is_open = lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file);

    {
        FILE *log = fopen("/tmp/logs/575.txt", "a");
        if (log) {
            fprintf(log,
                    "ASSERT_CHECK lfs_mlist_isopen called with head=%p cast_node=%p -> is_open=%d\n",
                    (void*)lfs->mlist, (void*)((struct lfs_mlist*)file), is_open);
            fclose(log);
        }
    }

    LFS_ASSERT(is_open);

    err = lfs_file_sync_(lfs, file);

    {
        FILE *log = fopen("/tmp/logs/575.txt", "a");
        if (log) {
            fprintf(log, "AFTER sync_ lfs=%p file=%p err=%d\n", (void*)lfs, (void*)file, err);
            fclose(log);
        }
    }

    LFS_TRACE("lfs_file_sync -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    {
        FILE *log = fopen("/tmp/logs/575.txt", "a");
        if (log) {
            fprintf(log, "EXIT lfs_file_sync: returning %d\n", err);
            fclose(log);
        }
    }

    return err;
}
