int lfs_file_sync(lfs_t *lfs, lfs_file_t *file) {
    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        return err;
    }

    LFS_TRACE("lfs_file_sync(%p, %p)", (void*)lfs, (void*)file);

    /* Probe: log control flow and state to /tmp/logs/575.txt so other agents
       can reproduce evidence for the reported const-correctness issue. */
    {
        FILE *log = fopen("/tmp/logs/575.txt", "a");
        if (log) {
            /* Record entry and pointers involved in the assertion that is
               suspected to violate MISRA 8.13 (non-const parameters and cast). */
            fprintf(log, "ENTER lfs_file_sync: lfs=%p file=%p mlist=%p\n",
                    (void*)lfs, (void*)file, (void*)lfs->mlist);

            /* Log that the code will call lfs_mlist_isopen using a cast that
               can mask const-correctness: (struct lfs_mlist*)file */
            fprintf(log, "About to call lfs_mlist_isopen(head=%p, node=(struct lfs_mlist*)%p)\n",
                    (void*)lfs->mlist, (void*)file);

            /* Call lfs_mlist_isopen once to capture its return value for the
               probe. We still call LFS_ASSERT below to preserve original
               behavior (which calls it again). */
            int isopen = lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file);
            fprintf(log, "lfs_mlist_isopen returned: %d\n", isopen);

            if (!isopen) {
                fprintf(log, "NOTE: lfs_mlist_isopen indicates the file is NOT in the open list\n");
            }

            fclose(log);
        }
    }

    /* Preserve original assertion (including the cast that is part of the
       reported violation scenario). */
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    err = lfs_file_sync_(lfs, file);

    /* Log the result of the sync operation as well. */
    {
        FILE *log = fopen("/tmp/logs/575.txt", "a");
        if (log) {
            fprintf(log, "lfs_file_sync -> %d\n", err);
            fclose(log);
        }
    }

    LFS_TRACE("lfs_file_sync -> %d", err);
    LFS_UNLOCK(lfs->cfg);
    return err;
}
