lfs_soff_t lfs_file_tell(lfs_t *lfs, lfs_file_t *file) {
    /* Probe log file to capture control flow and state for analysis */
    FILE * __lfs_log = fopen("/tmp/logs/319.txt", "a");

    int err = LFS_LOCK(lfs->cfg);
    if (__lfs_log) {
        fprintf(__lfs_log, "ENTER lfs_file_tell: lfs=%p file=%p LFS_LOCK returned err=%d\n",
                (void*)lfs, (void*)file, err);
        fflush(__lfs_log);
    }

    if (err) {
        if (__lfs_log) {
            fprintf(__lfs_log, "BRANCH: if(err) taken -> returning err=%d\n", err);
            fclose(__lfs_log);
        }
        return err;
    }

    if (__lfs_log) {
        fprintf(__lfs_log, "BRANCH: if(err) not taken -> continuing to lfs_file_tell_\n");
        fflush(__lfs_log);
    }

    LFS_TRACE("lfs_file_tell(%p, %p)", (void*)lfs, (void*)file);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_soff_t res = lfs_file_tell_(lfs, file);

    if (__lfs_log) {
        fprintf(__lfs_log, "lfs_file_tell_ returned res=%lld\n", (long long)res);
        fflush(__lfs_log);
    }

    LFS_TRACE("lfs_file_tell -> %"PRId32, res);
    LFS_UNLOCK(lfs->cfg);

    if (__lfs_log) {
        fprintf(__lfs_log, "LFS_UNLOCK called, returning res=%lld\n", (long long)res);
        fclose(__lfs_log);
    }

    return res;
}
