lfs_soff_t lfs_file_tell(lfs_t *lfs, lfs_file_t *file) {
    /* Preserve original behavior: obtain lock via macro */
    int err = LFS_LOCK(lfs->cfg);

    /* Probe logging: attempt to append control-flow/state info to /tmp/logs/316.txt */
    FILE *lfs_log = fopen("/tmp/logs/316.txt", "a");
    if (lfs_log) {
        fprintf(lfs_log, "lfs_file_tell: ENTER lfs=%p file=%p cfg=%p LFS_LOCK returned err=%d\n",
                (void*)lfs, (void*)file, (void*)lfs->cfg, err);
        fflush(lfs_log);
    }

    if (err) {
        /* Log the early-return path so callers/reproducers can see that this branch was taken. */
        if (lfs_log) {
            fprintf(lfs_log, "lfs_file_tell: early return due to err=%d\n\n", err);
            fclose(lfs_log);
        }
        return err;
    }

    /* Normal path continues: trace and assert as in original code. */
    if (lfs_log) {
        fprintf(lfs_log, "lfs_file_tell: lock succeeded, proceeding to LFS_TRACE and LFS_ASSERT\n");
        fflush(lfs_log);
    }
    LFS_TRACE("lfs_file_tell(%p, %p)", (void*)lfs, (void*)file);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    /* Call the real implementation and log its result. */
    lfs_soff_t res = lfs_file_tell_(lfs, file);

    if (lfs_log) {
        /* Cast to long long for a portable numeric print in the probe log. */
        fprintf(lfs_log, "lfs_file_tell: lfs_file_tell_ returned res=%lld\n", (long long)res);
        fflush(lfs_log);
    }

    LFS_TRACE("lfs_file_tell -> %"PRId32, res);
    LFS_UNLOCK(lfs->cfg);

    if (lfs_log) {
        fprintf(lfs_log, "lfs_file_tell: unlocked cfg=%p and returning res=%lld\n\n", (void*)lfs->cfg, (long long)res);
        fclose(lfs_log);
    }

    return res;
}
