lfs_soff_t lfs_file_tell(lfs_t *lfs, lfs_file_t *file) {
    int err = LFS_LOCK(lfs->cfg);

    /* Open log for appending control-flow and state information. */
    FILE *____log316 = fopen("/tmp/logs/316.txt", "a");
    if (____log316) {
        fprintf(____log316, "ENTER lfs_file_tell: lfs=%p file=%p\n", (void*)lfs, (void*)file);
        fprintf(____log316, "LFS_LOCK returned err=%d\n", err);
        /* Log address of the cfg member (always valid to take address) to help
           reproduce macro expansions that reference cfg. */
        fprintf(____log316, "&lfs->cfg=%p\n", (void*)&lfs->cfg);
    }

    if (err) {
        if (____log316) {
            fprintf(____log316, "err is non-zero -> early return with err=%d\n", err);
            fclose(____log316);
        }
        return err;
    }

    if (____log316) {
        fprintf(____log316, "lock acquired (err==0). Continuing into body.\n");
        fclose(____log316);
    }

    LFS_TRACE("lfs_file_tell(%p, %p)", (void*)lfs, (void*)file);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_soff_t res = lfs_file_tell_(lfs, file);

    /* Append result and unlock information to the same log */
    ____log316 = fopen("/tmp/logs/316.txt", "a");
    if (____log316) {
        fprintf(____log316, "lfs_file_tell_ returned res=%lld\n", (long long)res);
    }

    LFS_TRACE("lfs_file_tell -> %"PRId32, res);
    LFS_UNLOCK(lfs->cfg);

    if (____log316) {
        fprintf(____log316, "LFS_UNLOCK called. Returning res=%lld\n", (long long)res);
        fclose(____log316);
    }

    return res;
}
