lfs_soff_t lfs_file_tell(lfs_t *lfs, lfs_file_t *file) {
    /* Preserve original locking behavior */
    int err = LFS_LOCK(lfs->cfg);

    /* Open log file for appending probe information. If this fails, continue silently
       to preserve original behavior. */
    FILE *___lfs_log_fp = fopen("/tmp/logs/318.txt", "a");
    if (___lfs_log_fp) {
        fprintf(___lfs_log_fp, "ENTER lfs_file_tell: lfs=%p file=%p lock_ret=%d\n",
                (void*)lfs, (void*)file, err);
        fflush(___lfs_log_fp);
    }

    if (err) {
        if (___lfs_log_fp) {
            fprintf(___lfs_log_fp, "lfs_file_tell: lock failed, returning err=%d\n", err);
            fclose(___lfs_log_fp);
        }
        return err;
    }

    LFS_TRACE("lfs_file_tell(%p, %p)", (void*)lfs, (void*)file);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_soff_t res = lfs_file_tell_(lfs, file);

    if (___lfs_log_fp) {
        /* Log the result of the internal call. Use the same formatting macro the
           original code used for consistency. */
        fprintf(___lfs_log_fp, "lfs_file_tell_: res=%" PRId32 "\n", res);
        fflush(___lfs_log_fp);
    }

    LFS_TRACE("lfs_file_tell -> %"PRId32, res);

    if (___lfs_log_fp) {
        fprintf(___lfs_log_fp, "about to call LFS_UNLOCK(lfs->cfg)\n");
        fflush(___lfs_log_fp);
    }

    /* This is the statement reported as dead code in the given configuration. We
       still invoke it to preserve original semantics; logging before/after will
       provide evidence whether it has any observable effect at runtime. */
    LFS_UNLOCK(lfs->cfg);

    if (___lfs_log_fp) {
        fprintf(___lfs_log_fp, "after LFS_UNLOCK(lfs->cfg)\n");
        fclose(___lfs_log_fp);
    }

    return res;
}
