lfs_soff_t lfs_file_tell(lfs_t *lfs, lfs_file_t *file) {
    /* Preserve original locking behavior */
    int err = LFS_LOCK(lfs->cfg);

    /* Minimal local declarations for stdio functions to avoid adding headers */
    typedef struct FILE FILE;
    extern FILE *fopen(const char *path, const char *mode);
    extern int fprintf(FILE *stream, const char *format, ...);
    extern int fclose(FILE *stream);

    FILE *log = fopen("/tmp/logs/318.txt", "a");
    if (log) {
        fprintf(log, "ENTER lfs_file_tell: lfs=%p file=%p\n", (void*)lfs, (void*)file);
        fprintf(log, "LOCK result=%d\n", err);
    }

    if (err) {
        if (log) {
            fprintf(log, "LOCK failed, returning err=%d\n", err);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_file_tell(%p, %p)", (void*)lfs, (void*)file);
    if (log) {
        fprintf(log, "After LFS_TRACE and before LFS_ASSERT\n");
    }

    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    if (log) {
        fprintf(log, "Calling lfs_file_tell_ (delegate) with lfs=%p file=%p\n", (void*)lfs, (void*)file);
        fclose(log);
        log = NULL;
    }

    lfs_soff_t res = lfs_file_tell_(lfs, file);

    /* Re-open to append the result so the log shows the full control path */
    log = fopen("/tmp/logs/318.txt", "a");
    if (log) {
        fprintf(log, "Returned from lfs_file_tell_: res=%lld\n", (long long)res);
        fprintf(log, "Finalizing lfs_file_tell: unlocking and returning\n");
        fclose(log);
    }

    LFS_TRACE("lfs_file_tell -> %"PRId32, res);
    LFS_UNLOCK(lfs->cfg);
    return res;
}
