lfs_soff_t lfs_file_tell(lfs_t *lfs, lfs_file_t *file) {
    int err = LFS_LOCK(lfs->cfg);

    /* Open log file for probe output. Prefer the requested path. */
    FILE *probe_log = fopen("/tmp/logs/315.txt", "a");
    if (probe_log) {
        fprintf(probe_log, "ENTER lfs_file_tell: lfs=%p, file=%p, lock_err=%d\n",
                (void*)lfs, (void*)file, err);
        fflush(probe_log);
    }

    if (err) {
        if (probe_log) {
            fprintf(probe_log, "lfs_file_tell: LFS_LOCK returned non-zero err=%d, returning early\n", err);
            fclose(probe_log);
        }
        return err;
    }

    LFS_TRACE("lfs_file_tell(%p, %p)", (void*)lfs, (void*)file);

    /* Probe the specific cast that is noted in the violation scenario.
       Record the pointer values and the fact we are about to call lfs_mlist_isopen
       with a (struct lfs_mlist *) cast of file. This provides evidence of the
       cast-away-const usage at runtime. */
    if (probe_log) {
        fprintf(probe_log, "About to assert lfs_mlist_isopen with cast: (struct lfs_mlist*)file = %p\n",
                (void*)(struct lfs_mlist*)file);
        fflush(probe_log);
    }

    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_soff_t res = lfs_file_tell_(lfs, file);

    if (probe_log) {
        fprintf(probe_log, "lfs_file_tell_: result res=%lld\n", (long long)res);
        fprintf(probe_log, "lfs_file_tell: calling LFS_UNLOCK and returning\n");
        fclose(probe_log);
    }

    LFS_TRACE("lfs_file_tell -> %"PRId32, res);
    LFS_UNLOCK(lfs->cfg);
    return res;
}
