int lfs_file_truncate(lfs_t *lfs, lfs_file_t *file, lfs_off_t size) {
    int err = LFS_LOCK(lfs->cfg);

    /* Open probe log for diagnostic evidence about LFS_UNLOCK behavior */
    FILE *probe = fopen("/tmp/logs/398.txt", "a");
    if (probe) {
        fprintf(probe, "ENTER lfs_file_truncate: lfs=%p file=%p size=%" PRIu32 "\n",
                (void*)lfs, (void*)file, size);
        fprintf(probe, "After LFS_LOCK: err=%d lfs->cfg=%p\n",
                err, (void*)lfs->cfg);
    }

    if (err) {
        if (probe) {
            fprintf(probe, "LFS_LOCK failed, returning %d\n", err);
            fclose(probe);
        }
        return err;
    }

    LFS_TRACE("lfs_file_truncate(%p, %p, %"PRIu32")",
            (void*)lfs, (void*)file, size);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    err = lfs_file_truncate_(lfs, file, size);

    LFS_TRACE("lfs_file_truncate -> %d", err);

    if (probe) {
        /* Record state just before calling LFS_UNLOCK so we can show it has no observable effect */
        fprintf(probe, "Before LFS_UNLOCK: err=%d lfs->cfg=%p\n", err, (void*)lfs->cfg);
    }

    /* The call under inspection: may expand to a no-op such as ((void)lfs->cfg) */
    LFS_UNLOCK(lfs->cfg);

    if (probe) {
        /* Record state after LFS_UNLOCK to demonstrate whether any observable state changed */
        fprintf(probe, "After LFS_UNLOCK: lfs->cfg=%p\n", (void*)lfs->cfg);
        fprintf(probe, "EXIT lfs_file_truncate -> %d\n", err);
        fclose(probe);
    }

    return err;
}
