int lfs_file_truncate(lfs_t *lfs, lfs_file_t *file, lfs_off_t size) {
    int err = LFS_LOCK(lfs->cfg);

    /* Log entry and lock result */
    {
        FILE *logf = fopen("/tmp/logs/395.txt", "a");
        if (logf) {
            fprintf(logf,
                "ENTER lfs_file_truncate: lfs=%p file=%p size=%" PRIu32 " lock_err=%d\n",
                (void*)lfs, (void*)file, size, err);
            fclose(logf);
        }
    }

    if (err) {
        /* Log early return due to lock failure */
        FILE *logf = fopen("/tmp/logs/395.txt", "a");
        if (logf) {
            fprintf(logf, "EXIT lfs_file_truncate: lock failed err=%d\n", err);
            fclose(logf);
        }
        return err;
    }

    LFS_TRACE("lfs_file_truncate(%p, %p, %"PRIu32")",
            (void*)lfs, (void*)file, size);

    /*
     * Reproduce and log the cast to a non-const pointer described in the
     * violation scenario. We capture the result of lfs_mlist_isopen to
     * provide evidence while preserving the original assertion.
     */
    {
        struct lfs_mlist *ml = (struct lfs_mlist*)file; /* explicit non-const cast */
        int isopen = 0;

        /* Attempt to observe the open-check result (no side-effects expected).
         * We call the helper to capture its return value for logging; the
         * original ASSERT below still enforces the invariant.
         */
        isopen = lfs_mlist_isopen(lfs->mlist, ml);

        FILE *logf = fopen("/tmp/logs/395.txt", "a");
        if (logf) {
            fprintf(logf,
                "CAST CHECK: cast (struct lfs_mlist *)file -> %p, lfs->mlist=%p, lfs_mlist_isopen=%d\n",
                (void*)ml, (void*)lfs->mlist, isopen);
            fclose(logf);
        }

        /* Preserve the original assertion (may abort in debug builds). */
        LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));
    }

    err = lfs_file_truncate_(lfs, file, size);

    /* Log result before unlocking */
    {
        FILE *logf = fopen("/tmp/logs/395.txt", "a");
        if (logf) {
            fprintf(logf, "lfs_file_truncate result: err=%d\n", err);
            fclose(logf);
        }
    }

    LFS_TRACE("lfs_file_truncate -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    /* Final exit log */
    {
        FILE *logf = fopen("/tmp/logs/395.txt", "a");
        if (logf) {
            fprintf(logf, "EXIT lfs_file_truncate: returning %d\n", err);
            fclose(logf);
        }
    }

    return err;
}
