static int lfs_file_truncate_(lfs_t *lfs, lfs_file_t *file, lfs_off_t size) {
    /* Open log for tracing control flow and state for Rule 2.2 investigation */
    FILE *logf = fopen("/tmp/logs/968.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER lfs_file_truncate_: lfs=%p file=%p size=%lld file->pos=%lld flags=0x%08x inline_max=%lld\n",
                (void*)lfs, (void*)file, (long long)size, (long long)file->pos, (unsigned)file->flags, (long long)lfs->inline_max);
        fclose(logf);
    }

    LFS_ASSERT((file->flags & LFS_O_WRONLY) == LFS_O_WRONLY);

    if (size > LFS_FILE_MAX) {
        logf = fopen("/tmp/logs/968.txt", "a");
        if (logf) {
            fprintf(logf, "RETURN LFS_ERR_INVAL: requested size %lld > LFS_FILE_MAX\n", (long long)size);
            fclose(logf);
        }
        return LFS_ERR_INVAL;
    }

    lfs_off_t pos = file->pos;
    lfs_off_t oldsize = lfs_file_size_(lfs, file);

    logf = fopen("/tmp/logs/968.txt", "a");
    if (logf) {
        fprintf(logf, "After lfs_file_size_: oldsize=%lld (called with lfs=%p)\n", (long long)oldsize, (void*)lfs);
        /* Note: the reported violation concerns dead code inside lfs_file_size_,
           specifically statements like '(void)lfs;'. This log demonstrates that
           lfs_file_size_ was invoked with lfs=%p and returned oldsize=%lld.\n", (void*)lfs, (long long)oldsize); */
        fclose(logf);
    }

    if (size < oldsize) {
        logf = fopen("/tmp/logs/968.txt", "a");
        if (logf) {
            fprintf(logf, "Branch: size < oldsize (size=%lld oldsize=%lld)\n", (long long)size, (long long)oldsize);
            fclose(logf);
        }
        // revert to inline file?
        if (size <= lfs->inline_max) {
            logf = fopen("/tmp/logs/968.txt", "a");
            if (logf) {
                fprintf(logf, "Sub-branch: revert to inline (size=%lld <= inline_max=%lld)\n", (long long)size, (long long)lfs->inline_max);
                fclose(logf);
            }
            // flush+seek to head
            lfs_soff_t res = lfs_file_seek_(lfs, file, 0, LFS_SEEK_SET);
            if (res < 0) {
                logf = fopen("/tmp/logs/968.txt", "a");
                if (logf) {
                    fprintf(logf, "ERROR: lfs_file_seek_ to head failed: %lld\n", (long long)res);
                    fclose(logf);
                }
                return (int)res;
            }

            // read our data into rcache temporarily
            lfs_cache_drop(lfs, &lfs->rcache);
            res = lfs_file_flushedread(lfs, file,
                    lfs->rcache.buffer, size);
            if (res < 0) {
                logf = fopen("/tmp/logs/968.txt", "a");
                if (logf) {
                    fprintf(logf, "ERROR: lfs_file_flushedread failed: %lld\n", (long long)res);
                    fclose(logf);
                }
                return (int)res;
            }

            file->ctz.head = LFS_BLOCK_INLINE;
            file->ctz.size = size;
            file->flags |= LFS_F_DIRTY | LFS_F_READING | LFS_F_INLINE;
            file->cache.block = file->ctz.head;
            file->cache.off = 0;
            file->cache.size = lfs->cfg->cache_size;
            memcpy(file->cache.buffer, lfs->rcache.buffer, size);

            logf = fopen("/tmp/logs/968.txt", "a");
            if (logf) {
                fprintf(logf, "Reverted to inline: head=%lld size=%lld flags=0x%08x\n",
                        (long long)file->ctz.head, (long long)file->ctz.size, (unsigned)file->flags);
                fclose(logf);
            }

        } else {
            logf = fopen("/tmp/logs/968.txt", "a");
            if (logf) {
                fprintf(logf, "Sub-branch: shrink non-inline (size=%lld > inline_max=%lld)\n", (long long)size, (long long)lfs->inline_max);
                fclose(logf);
            }
            // need to flush since directly changing metadata
            int err = lfs_file_flush(lfs, file);
            if (err) {
                logf = fopen("/tmp/logs/968.txt", "a");
                if (logf) {
                    fprintf(logf, "ERROR: lfs_file_flush failed: %d\n", err);
                    fclose(logf);
                }
                return err;
            }

            // lookup new head in ctz skip list
            err = lfs_ctz_find(lfs, NULL, &file->cache,
                    file->ctz.head, file->ctz.size,
                    size-1, &file->block, &(lfs_off_t){0});
            if (err) {
                logf = fopen("/tmp/logs/968.txt", "a");
                if (logf) {
                    fprintf(logf, "ERROR: lfs_ctz_find failed: %d\n", err);
                    fclose(logf);
                }
                return err;
            }

            // need to set pos/block/off consistently so seeking back to
            // the old position does not get confused
            file->pos = size;
            file->ctz.head = file->block;
            file->ctz.size = size;
            file->flags |= LFS_F_DIRTY | LFS_F_READING;

            logf = fopen("/tmp/logs/968.txt", "a");
            if (logf) {
                fprintf(logf, "Shrink non-inline complete: new head=%lld size=%lld pos=%lld flags=0x%08x\n",
                        (long long)file->ctz.head, (long long)file->ctz.size, (long long)file->pos, (unsigned)file->flags);
                fclose(logf);
            }
        }
    } else if (size > oldsize) {
        logf = fopen("/tmp/logs/968.txt", "a");
        if (logf) {
            fprintf(logf, "Branch: size > oldsize (size=%lld oldsize=%lld) - will extend with zeros\n", (long long)size, (long long)oldsize);
            fclose(logf);
        }
        // flush+seek if not already at end
        lfs_soff_t res = lfs_file_seek_(lfs, file, 0, LFS_SEEK_END);
        if (res < 0) {
            logf = fopen("/tmp/logs/968.txt", "a");
            if (logf) {
                fprintf(logf, "ERROR: lfs_file_seek_ to end failed: %lld\n", (long long)res);
                fclose(logf);
            }
            return (int)res;
        }

        // fill with zeros
        while (file->pos < size) {
            res = lfs_file_write_(lfs, file, &(uint8_t){0}, 1);
            if (res < 0) {
                logf = fopen("/tmp/logs/968.txt", "a");
                if (logf) {
                    fprintf(logf, "ERROR: lfs_file_write_ failed while extending: %lld pos=%lld target=%lld\n", (long long)res, (long long)file->pos, (long long)size);
                    fclose(logf);
                }
                return (int)res;
            }
        }

        logf = fopen("/tmp/logs/968.txt", "a");
        if (logf) {
            fprintf(logf, "Extend complete: pos=%lld target=%lld\n", (long long)file->pos, (long long)size);
            fclose(logf);
        }
    }

    // restore pos
    lfs_soff_t res = lfs_file_seek_(lfs, file, pos, LFS_SEEK_SET);
    if (res < 0) {
      logf = fopen("/tmp/logs/968.txt", "a");
      if (logf) {
          fprintf(logf, "ERROR: restore pos failed: %lld (pos requested=%lld)\n", (long long)res, (long long)pos);
          fclose(logf);
      }
      return (int)res;
    }

    logf = fopen("/tmp/logs/968.txt", "a");
    if (logf) {
        fprintf(logf, "EXIT lfs_file_truncate_: success (restored pos=%lld)\n", (long long)pos);
        fclose(logf);
    }

    return 0;
}
