static int lfs_file_truncate_(lfs_t *lfs, lfs_file_t *file, lfs_off_t size) {
    /* Logging: append logs to /tmp/logs/965.txt to capture control flow & state */
    FILE *logf = fopen("/tmp/logs/965.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER lfs_file_truncate_: lfs=%p file=%p size=%lld\n",
                (void*)lfs, (void*)file, (long long)size);
        fflush(logf);
    }

    int _ret = 0;

    LFS_ASSERT((file->flags & LFS_O_WRONLY) == LFS_O_WRONLY);

    if (size > LFS_FILE_MAX) {
        if (logf) {
            fprintf(logf, "size > LFS_FILE_MAX: size=%lld LFS_FILE_MAX=%lld -> returning LFS_ERR_INVAL\n",
                    (long long)size, (long long)LFS_FILE_MAX);
            fflush(logf);
        }
        _ret = LFS_ERR_INVAL;
        goto _cleanup;
    }

    lfs_off_t pos = file->pos;
    lfs_off_t oldsize = lfs_file_size_(lfs, file);
    if (logf) {
        fprintf(logf, "state: pos=%lld oldsize=%lld size=%lld flags=0x%08x\n",
                (long long)pos, (long long)oldsize, (long long)size, (unsigned)file->flags);
        fflush(logf);
    }

    if (size < oldsize) {
        /* revert to inline file? */
        if (size <= lfs->inline_max) {
            /* flush+seek to head */
            lfs_soff_t res = lfs_file_seek_(lfs, file, 0, LFS_SEEK_SET);
            if (logf) {
                fprintf(logf, "seek to head returned %lld\n", (long long)res);
                fflush(logf);
            }
            if (res < 0) {
                _ret = (int)res;
                goto _cleanup;
            }

            /* read our data into rcache temporarily */
            lfs_cache_drop(lfs, &lfs->rcache);
            res = lfs_file_flushedread(lfs, file,
                    lfs->rcache.buffer, size);
            if (logf) {
                fprintf(logf, "flushedread returned %lld (size=%lld)\n", (long long)res, (long long)size);
                fflush(logf);
            }
            if (res < 0) {
                _ret = (int)res;
                goto _cleanup;
            }

            file->ctz.head = LFS_BLOCK_INLINE;
            file->ctz.size = size;
            file->flags |= LFS_F_DIRTY | LFS_F_READING | LFS_F_INLINE;
            file->cache.block = file->ctz.head;
            file->cache.off = 0;
            file->cache.size = lfs->cfg->cache_size;
            memcpy(file->cache.buffer, lfs->rcache.buffer, size);

            if (logf) {
                fprintf(logf, "converted to inline: ctz.head=%lld ctz.size=%lld\n",
                        (long long)file->ctz.head, (long long)file->ctz.size);
                fflush(logf);
            }

        } else {
            /* need to flush since directly changing metadata */
            int err = lfs_file_flush(lfs, file);
            if (logf) {
                fprintf(logf, "lfs_file_flush returned %d\n", err);
                fflush(logf);
            }
            if (err) {
                _ret = err;
                goto _cleanup;
            }

            /* lookup new head in ctz skip list */
            if (logf) {
                /* Log the use of a compound literal which will be passed in the call below. */
                lfs_off_t *tmp_cl = &(lfs_off_t){0};
                fprintf(logf, "about to call lfs_ctz_find: file->ctz.head=%lld file->ctz.size=%lld size-1=%lld compound_literal_addr=%p compound_literal_val=%lld\n",
                        (long long)file->ctz.head, (long long)file->ctz.size, (long long)(size-1),
                        (void*)tmp_cl, (long long)*tmp_cl);
                fflush(logf);
            }

            err = lfs_ctz_find(lfs, NULL, &file->cache,
                    file->ctz.head, file->ctz.size,
                    size-1, &file->block, &(lfs_off_t){0});

            if (logf) {
                fprintf(logf, "lfs_ctz_find returned %d, file->block=%lld\n",
                        err, (long long)file->block);
                fflush(logf);
            }

            if (err) {
                _ret = err;
                goto _cleanup;
            }

            /* need to set pos/block/off consistently so seeking back to
             * the old position does not get confused */
            file->pos = size;
            file->ctz.head = file->block;
            file->ctz.size = size;
            file->flags |= LFS_F_DIRTY | LFS_F_READING;

            if (logf) {
                fprintf(logf, "after ctz_find: pos=%lld ctz.head=%lld ctz.size=%lld flags=0x%08x\n",
                        (long long)file->pos, (long long)file->ctz.head, (long long)file->ctz.size, (unsigned)file->flags);
                fflush(logf);
            }
        }
    } else if (size > oldsize) {
        /* flush+seek if not already at end */
        lfs_soff_t res = lfs_file_seek_(lfs, file, 0, LFS_SEEK_END);
        if (logf) {
            fprintf(logf, "seek to end returned %lld\n", (long long)res);
            fflush(logf);
        }
        if (res < 0) {
            _ret = (int)res;
            goto _cleanup;
        }

        /* fill with zeros */
        while (file->pos < size) {
            res = lfs_file_write_(lfs, file, &(uint8_t){0}, 1);
            if (logf) {
                fprintf(logf, "write zero returned %lld, file->pos now=%lld (target size=%lld)\n",
                        (long long)res, (long long)file->pos, (long long)size);
                fflush(logf);
            }
            if (res < 0) {
                _ret = (int)res;
                goto _cleanup;
            }
        }
    }

    /* restore pos */
    {
        lfs_soff_t res = lfs_file_seek_(lfs, file, pos, LFS_SEEK_SET);
        if (logf) {
            fprintf(logf, "restore pos returned %lld, restoring pos to %lld\n",
                    (long long)res, (long long)pos);
            fflush(logf);
        }
        if (res < 0) {
            _ret = (int)res;
            goto _cleanup;
        }
    }

    _ret = 0;

_cleanup:
    if (logf) {
        fprintf(logf, "EXIT lfs_file_truncate_: return=%d final_pos=%lld final_ctz_size=%lld flags=0x%08x\n",
                _ret, (long long)file->pos, (long long)file->ctz.size, (unsigned)file->flags);
        fclose(logf);
    }
    return _ret;
}
