static int lfs_file_truncate_(lfs_t *lfs, lfs_file_t *file, lfs_off_t size) {
    /* Logging probe: append control-flow and state information to /tmp/logs/965.txt */
    do {
        FILE *log = fopen("/tmp/logs/965.txt", "a");
        if (log) {
            fprintf(log, "ENTER lfs_file_truncate_: lfs=%p file=%p requested_size=%lld\n",
                    (void*)lfs, (void*)file, (long long)size);
            if (file) {
                fprintf(log, "  file->pos=%lld file->flags=0x%08x\n",
                        (long long)file->pos, (unsigned)file->flags);
            }
            fclose(log);
        }
    } while (0);

    LFS_ASSERT((file->flags & LFS_O_WRONLY) == LFS_O_WRONLY);

    if (size > LFS_FILE_MAX) {
        do {
            FILE *log = fopen("/tmp/logs/965.txt", "a");
            if (log) {
                fprintf(log, "EXIT lfs_file_truncate_: size > LFS_FILE_MAX (%lld > %lld) => LFS_ERR_INVAL\n",
                        (long long)size, (long long)LFS_FILE_MAX);
                fclose(log);
            }
        } while (0);
        return LFS_ERR_INVAL;
    }

    lfs_off_t pos = file->pos;
    lfs_off_t oldsize = lfs_file_size_(lfs, file);

    do {
        FILE *log = fopen("/tmp/logs/965.txt", "a");
        if (log) {
            fprintf(log, "After size check: pos=%lld oldsize=%lld\n",
                    (long long)pos, (long long)oldsize);
            fclose(log);
        }
    } while (0);

    if (size < oldsize) {
        // revert to inline file?
        if (size <= lfs->inline_max) {
            do {
                FILE *log = fopen("/tmp/logs/965.txt", "a");
                if (log) {
                    fprintf(log, "Branch: size < oldsize AND size <= inline_max (size=%lld inline_max=%lld)\n",
                            (long long)size, (long long)lfs->inline_max);
                    fclose(log);
                }
            } while (0);

            // flush+seek to head
            lfs_soff_t res = lfs_file_seek_(lfs, file, 0, LFS_SEEK_SET);
            if (res < 0) {
                do {
                    FILE *log = fopen("/tmp/logs/965.txt", "a");
                    if (log) {
                        fprintf(log, "lfs_file_seek_ failed with %lld\n", (long long)res);
                        fclose(log);
                    }
                } while (0);
                return (int)res;
            }

            // read our data into rcache temporarily
            lfs_cache_drop(lfs, &lfs->rcache);
            res = lfs_file_flushedread(lfs, file,
                    lfs->rcache.buffer, size);
            if (res < 0) {
                do {
                    FILE *log = fopen("/tmp/logs/965.txt", "a");
                    if (log) {
                        fprintf(log, "lfs_file_flushedread failed with %lld\n", (long long)res);
                        fclose(log);
                    }
                } while (0);
                return (int)res;
            }

            file->ctz.head = LFS_BLOCK_INLINE;
            file->ctz.size = size;
            file->flags |= LFS_F_DIRTY | LFS_F_READING | LFS_F_INLINE;
            file->cache.block = file->ctz.head;
            file->cache.off = 0;
            file->cache.size = lfs->cfg->cache_size;
            memcpy(file->cache.buffer, lfs->rcache.buffer, size);

        } else {
            do {
                FILE *log = fopen("/tmp/logs/965.txt", "a");
                if (log) {
                    fprintf(log, "Branch: size < oldsize AND size > inline_max (size=%lld inline_max=%lld)\n",
                            (long long)size, (long long)lfs->inline_max);
                    fclose(log);
                }
            } while (0);

            // need to flush since directly changing metadata
            int err = lfs_file_flush(lfs, file);
            if (err) {
                do {
                    FILE *log = fopen("/tmp/logs/965.txt", "a");
                    if (log) {
                        fprintf(log, "lfs_file_flush failed with %d\n", err);
                        fclose(log);
                    }
                } while (0);
                return err;
            }

            // lookup new head in ctz skip list
            do {
                FILE *log = fopen("/tmp/logs/965.txt", "a");
                if (log) {
                    /* We log that we're about to call lfs_ctz_find with a compound literal
                       for the tail argument ("&(lfs_off_t){0}"). This mirrors the original
                       code path that motivated the MISRA Rule 8.13 concern. */
                    fprintf(log, "Calling lfs_ctz_find: cache=%p head=%lld size=%lld target_index=%lld tail_ptr=(address of compound literal)\n",
                            (void*)&file->cache, (long long)file->ctz.head, (long long)file->ctz.size, (long long)(size-1));

                    /* Additionally, we create a temporary compound literal pointer to log
                       its raw pointer value for debugging evidence. Note: this creates a
                       separate compound literal and is only used for logging. The actual
                       call below still uses the original compound literal as in the
                       source code. */
                    lfs_off_t *log_tmp = (lfs_off_t[]){0};
                    fprintf(log, "  log_tmp pointer=%p value=%lld\n", (void*)log_tmp, (long long)*log_tmp);
                    fclose(log);
                }
            } while (0);

            err = lfs_ctz_find(lfs, NULL, &file->cache,
                    file->ctz.head, file->ctz.size,
                    size-1, &file->block, &(lfs_off_t){0});
            if (err) {
                do {
                    FILE *log = fopen("/tmp/logs/965.txt", "a");
                    if (log) {
                        fprintf(log, "lfs_ctz_find failed with %d\n", err);
                        fclose(log);
                    }
                } while (0);
                return err;
            }

            // need to set pos/block/off consistently so seeking back to
            // the old position does not get confused
            file->pos = size;
            file->ctz.head = file->block;
            file->ctz.size = size;
            file->flags |= LFS_F_DIRTY | LFS_F_READING;
        }
    } else if (size > oldsize) {
        do {
            FILE *log = fopen("/tmp/logs/965.txt", "a");
            if (log) {
                fprintf(log, "Branch: size > oldsize (size=%lld oldsize=%lld)\n",
                        (long long)size, (long long)oldsize);
                fclose(log);
            }
        } while (0);

        // flush+seek if not already at end
        lfs_soff_t res = lfs_file_seek_(lfs, file, 0, LFS_SEEK_END);
        if (res < 0) {
            do {
                FILE *log = fopen("/tmp/logs/965.txt", "a");
                if (log) {
                    fprintf(log, "lfs_file_seek_ (SEEK_END) failed with %lld\n", (long long)res);
                    fclose(log);
                }
            } while (0);
            return (int)res;
        }

        // fill with zeros
        while (file->pos < size) {
            res = lfs_file_write_(lfs, file, &(uint8_t){0}, 1);
            if (res < 0) {
                do {
                    FILE *log = fopen("/tmp/logs/965.txt", "a");
                    if (log) {
                        fprintf(log, "lfs_file_write_ failed while filling zeros with %lld\n", (long long)res);
                        fclose(log);
                    }
                } while (0);
                return (int)res;
            }
        }
    }

    // restore pos
    lfs_soff_t res = lfs_file_seek_(lfs, file, pos, LFS_SEEK_SET);
    if (res < 0) {
      do {
          FILE *log = fopen("/tmp/logs/965.txt", "a");
          if (log) {
              fprintf(log, "Final lfs_file_seek_ failed with %lld\n", (long long)res);
              fclose(log);
          }
      } while (0);
      return (int)res;
    }

    do {
        FILE *log = fopen("/tmp/logs/965.txt", "a");
        if (log) {
            fprintf(log, "EXIT lfs_file_truncate_: success, restored pos=%lld\n", (long long)pos);
            fclose(log);
        }
    } while (0);

    return 0;
}
