int lfs_format(lfs_t *lfs, const struct lfs_config *cfg) {
    int err = LFS_LOCK(cfg);

    /* Probe: record entry and the result of LFS_LOCK to reproduce invariant/constant behavior */
    do {
        FILE *f = fopen("/tmp/logs/739.txt", "a");
        if (f) {
            fprintf(f, "ENTER lfs_format: lfs=%p, cfg=%p\n", (void*)lfs, (void*)cfg);
            fprintf(f, "LFS_LOCK(cfg) returned: %d\n", err);
            fclose(f);
        }
    } while (0);

    if (err) {
        /* Probe: log early return when lock failed/indicated error */
        do {
            FILE *f = fopen("/tmp/logs/739.txt", "a");
            if (f) {
                fprintf(f, "EARLY RETURN from lfs_format: err=%d (after LFS_LOCK)\n", err);
                fclose(f);
            }
        } while (0);
        return err;
    }

    LFS_TRACE("lfs_format(%p, %p {.context=%p, "
                ".read=%p, .prog=%p, .erase=%p, .sync=%p, "
                ".read_size=%"PRIu32", .prog_size=%"PRIu32", "
                ".block_size=%"PRIu32", .block_count=%"PRIu32", "
                ".block_cycles=%"PRId32", .cache_size=%"PRIu32", "
                ".lookahead_size=%"PRIu32", .read_buffer=%p, "
                ".prog_buffer=%p, .lookahead_buffer=%p, "
                ".name_max=%"PRIu32", .file_max=%"PRIu32", "
                ".attr_max=%"PRIu32"}),",
            (void*)lfs, (void*)cfg, cfg->context,
            (void*)(uintptr_t)cfg->read, (void*)(uintptr_t)cfg->prog,
            (void*)(uintptr_t)cfg->erase, (void*)(uintptr_t)cfg->sync,
            cfg->read_size, cfg->prog_size, cfg->block_size, cfg->block_count,
            cfg->block_cycles, cfg->cache_size, cfg->lookahead_size,
            cfg->read_buffer, cfg->prog_buffer, cfg->lookahead_buffer,
            cfg->name_max, cfg->file_max, cfg->attr_max);

    /* Probe: dump a compact, machine-readable snapshot of relevant cfg fields to file */
    do {
        FILE *f = fopen("/tmp/logs/739.txt", "a");
        if (f) {
            fprintf(f, "CFG SNAPSHOT: context=%p, read=%p, prog=%p, erase=%p, sync=%p\n",
                    cfg->context,
                    (void*)(uintptr_t)cfg->read, (void*)(uintptr_t)cfg->prog,
                    (void*)(uintptr_t)cfg->erase, (void*)(uintptr_t)cfg->sync);
            fprintf(f, "CFG SIZES: read_size=%llu, prog_size=%llu, block_size=%llu, block_count=%llu, block_cycles=%lld\n",
                    (unsigned long long)cfg->read_size,
                    (unsigned long long)cfg->prog_size,
                    (unsigned long long)cfg->block_size,
                    (unsigned long long)cfg->block_count,
                    (long long)cfg->block_cycles);
            fprintf(f, "CFG BUFFERS: cache_size=%llu, lookahead_size=%llu, read_buffer=%p, prog_buffer=%p, lookahead_buffer=%p\n",
                    (unsigned long long)cfg->cache_size,
                    (unsigned long long)cfg->lookahead_size,
                    cfg->read_buffer, cfg->prog_buffer, cfg->lookahead_buffer);
            fprintf(f, "CFG LIMITS: name_max=%llu, file_max=%llu, attr_max=%llu\n",
                    (unsigned long long)cfg->name_max,
                    (unsigned long long)cfg->file_max,
                    (unsigned long long)cfg->attr_max);
            fclose(f);
        }
    } while (0);

    err = lfs_format_(lfs, cfg);

    /* Probe: record outcome of lfs_format_ and that we're about to unlock */
    do {
        FILE *f = fopen("/tmp/logs/739.txt", "a");
        if (f) {
            fprintf(f, "CALL lfs_format_ returned: %d\n", err);
            fprintf(f, "About to call LFS_UNLOCK(cfg)\n");
            fclose(f);
        }
    } while (0);

    LFS_TRACE("lfs_format -> %d", err);
    LFS_UNLOCK(cfg);

    /* Probe: final exit log */
    do {
        FILE *f = fopen("/tmp/logs/739.txt", "a");
        if (f) {
            fprintf(f, "EXIT lfs_format: returning %d\n", err);
            fclose(f);
        }
    } while (0);

    return err;
}
