int lfs_format(lfs_t *lfs, const struct lfs_config *cfg) {
    /* Probe logging to capture control flow and state for Rule 2.1 investigation.
       Logs appended to /tmp/logs/736.txt so external test harnesses can inspect them. */
    int err = LFS_LOCK(cfg);

    /* Try to open log file; if unavailable, fall back to LFS_TRACE so tests still get some info. */
    FILE *___lfs_probe_fp = fopen("/tmp/logs/736.txt", "a");
    if (___lfs_probe_fp) {
        fprintf(___lfs_probe_fp, "ENTER lfs_format: lfs=%p cfg=%p\n", (void*)lfs, (void*)cfg);
        fprintf(___lfs_probe_fp, "AFTER LFS_LOCK: err=%d\n", err);
        if (err) {
            fprintf(___lfs_probe_fp, "if (err) branch TAKEN: returning %d (early return)\n", err);
            fclose(___lfs_probe_fp);
            return err;
        }
        fprintf(___lfs_probe_fp, "if (err) branch NOT TAKEN: continuing\n");
        fclose(___lfs_probe_fp);
    } else {
        /* Ensure we record at least minimal trace if fopen fails. */
        LFS_TRACE("lfs_format probe: fopen(/tmp/logs/736.txt) failed; LFS_LOCK returned %d", err);
        if (err) {
            LFS_TRACE("lfs_format: early return %d", err);
            return err;
        }
    }

    LFS_TRACE("lfs_format(%p, %p {.context=%p, "
                ".read=%p, .prog=%p, .erase=%p, .sync=%p, "
                ".read_size=%"PRIu32", .prog_size=%"PRIu32", "
                ".block_size=%"PRIu32", .block_count=%"PRIu32", "
                ".block_cycles=%"PRId32", .cache_size=%"PRIu32", "
                ".lookahead_size=%"PRIu32", .read_buffer=%p, "
                ".prog_buffer=%p, .lookahead_buffer=%p, "
                ".name_max=%"PRIu32", .file_max=%"PRIu32", "
                ".attr_max=%"PRIu32"}),",
            (void*)lfs, (void*)cfg, cfg->context,
            (void*)(uintptr_t)cfg->read, (void*)(uintptr_t)cfg->prog,
            (void*)(uintptr_t)cfg->erase, (void*)(uintptr_t)cfg->sync,
            cfg->read_size, cfg->prog_size, cfg->block_size, cfg->block_count,
            cfg->block_cycles, cfg->cache_size, cfg->lookahead_size,
            cfg->read_buffer, cfg->prog_buffer, cfg->lookahead_buffer,
            cfg->name_max, cfg->file_max, cfg->attr_max);

    err = lfs_format_(lfs, cfg);

    /* Log result of lfs_format_ */
    ___lfs_probe_fp = fopen("/tmp/logs/736.txt", "a");
    if (___lfs_probe_fp) {
        fprintf(___lfs_probe_fp, "AFTER lfs_format_: err=%d\n", err);
        fclose(___lfs_probe_fp);
    } else {
        LFS_TRACE("lfs_format: could not open probe log to write result; err=%d", err);
    }

    LFS_TRACE("lfs_format -> %d", err);
    LFS_UNLOCK(cfg);
    return err;
}
