int lfs_format(lfs_t *lfs, const struct lfs_config *cfg) {
    int err = LFS_LOCK(cfg);
    if (err) {
        /* Log lock failure for debugging */
        FILE *f = fopen("/tmp/logs/738.txt", "a");
        if (f) {
            fprintf(f, "lfs_format: LFS_LOCK failed -> %d, lfs=%p, cfg=%p\n",
                    err, (void*)lfs, (void*)cfg);
            fclose(f);
        }
        return err;
    }
    LFS_TRACE("lfs_format(%p, %p {.context=%p, "
                ".read=%p, .prog=%p, .erase=%p, .sync=%p, "
                ".read_size=%"PRIu32", .prog_size=%"PRIu32", "
                ".block_size=%"PRIu32", .block_count=%"PRIu32", "
                ".block_cycles=%"PRId32", .cache_size=%"PRIu32", "
                ".lookahead_size=%"PRIu32", .read_buffer=%p, "
                ".prog_buffer=%p, .lookahead_buffer=%p, "
                ".name_max=%"PRIu32", .file_max=%"PRIu32", "
                ".attr_max=%"PRIu32"}),",
            (void*)lfs, (void*)cfg, cfg->context,
            (void*)(uintptr_t)cfg->read, (void*)(uintptr_t)cfg->prog,
            (void*)(uintptr_t)cfg->erase, (void*)(uintptr_t)cfg->sync,
            cfg->read_size, cfg->prog_size, cfg->block_size, cfg->block_count,
            cfg->block_cycles, cfg->cache_size, cfg->lookahead_size,
            cfg->read_buffer, cfg->prog_buffer, cfg->lookahead_buffer,
            cfg->name_max, cfg->file_max, cfg->attr_max);

    err = lfs_format_(lfs, cfg);

    LFS_TRACE("lfs_format -> %d", err);

    /* Probe logging to demonstrate behavior (and capture potential no-op of LFS_UNLOCK) */
    FILE *f = fopen("/tmp/logs/738.txt", "a");
    if (f) {
        fprintf(f, "lfs_format: before LFS_UNLOCK -> lfs=%p, cfg=%p, err=%d\n",
                (void*)lfs, (void*)cfg, err);
        if (cfg) {
            fprintf(f, "  cfg snapshot: context=%p, read=%p, prog=%p, erase=%p, sync=%p\n",
                    cfg->context,
                    (void*)(uintptr_t)cfg->read, (void*)(uintptr_t)cfg->prog,
                    (void*)(uintptr_t)cfg->erase, (void*)(uintptr_t)cfg->sync);
            fprintf(f, "  sizes: read_size=%"PRIu32", prog_size=%"PRIu32", block_size=%"PRIu32"\n",
                    cfg->read_size, cfg->prog_size, cfg->block_size);
            fflush(f);
        } else {
            fprintf(f, "  cfg is NULL\n");
            fflush(f);
        }
    }

    /* Capture some observable fields to compare before/after unlock */
    void *ctx_before = cfg ? cfg->context : NULL;
    uint32_t read_size_before = cfg ? cfg->read_size : 0;

    /* Call the unlock (may be a no-op depending on macro expansion) */
    LFS_UNLOCK(cfg);

    /* Log after unlock to allow reproduction/evidence that unlock had no effect */
    if (f) {
        void *ctx_after = cfg ? cfg->context : NULL;
        uint32_t read_size_after = cfg ? cfg->read_size : 0;
        fprintf(f, "lfs_format: after LFS_UNLOCK -> ctx_before=%p, ctx_after=%p, read_size_before=%"PRIu32", read_size_after=%"PRIu32"\n",
                ctx_before, ctx_after, read_size_before, read_size_after);
        if (ctx_before == ctx_after && read_size_before == read_size_after) {
            fprintf(f, "  Observation: no observable change after LFS_UNLOCK (possible no-op)\n");
        } else {
            fprintf(f, "  Observation: cfg members changed after LFS_UNLOCK\n");
        }
        fclose(f);
    }

    return err;
}
