int lfs_format(lfs_t *lfs, const struct lfs_config *cfg) {
    int err = LFS_LOCK(cfg);

    /* Open log file early to capture entry and early failures. */
    FILE *logf = fopen("/tmp/logs/733.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER lfs_format: lfs=%p, cfg=%p\n", (void*)lfs, (void*)cfg);
        fflush(logf);
    }

    if (err) {
        if (logf) {
            fprintf(logf, "LFS_LOCK failed: err=%d\n", err);
            fclose(logf);
        }
        return err;
    }

    LFS_TRACE("lfs_format(%p, %p {.context=%p, "
                ".read=%p, .prog=%p, .erase=%p, .sync=%p, "
                ".read_size=%"PRIu32", .prog_size=%"PRIu32", "
                ".block_size=%"PRIu32", .block_count=%"PRIu32", "
                ".block_cycles=%"PRId32", .cache_size=%"PRIu32", "
                ".lookahead_size=%"PRIu32", .read_buffer=%p, "
                ".prog_buffer=%p, .lookahead_buffer=%p, "
                ".name_max=%"PRIu32", .file_max=%"PRIu32", "
                ".attr_max=%"PRIu32"}),",
            (void*)lfs, (void*)cfg, cfg->context,
            (void*)(uintptr_t)cfg->read, (void*)(uintptr_t)cfg->prog,
            (void*)(uintptr_t)cfg->erase, (void*)(uintptr_t)cfg->sync,
            cfg->read_size, cfg->prog_size, cfg->block_size, cfg->block_count,
            cfg->block_cycles, cfg->cache_size, cfg->lookahead_size,
            cfg->read_buffer, cfg->prog_buffer, cfg->lookahead_buffer,
            cfg->name_max, cfg->file_max, cfg->attr_max);

    /* Additional persistent logging to reproduce potential OOB scenario.
       We avoid dereferencing any user-provided buffers to prevent altering
       behavior; we only record pointer values and size parameters. */
    if (logf) {
        fprintf(logf, "CFG state: read_buffer=%p, prog_buffer=%p, lookahead_buffer=%p\n",
                (void*)cfg->read_buffer, (void*)cfg->prog_buffer, (void*)cfg->lookahead_buffer);
        fprintf(logf, "CFG sizes: read_size=%lu, prog_size=%lu, cache_size=%lu, lookahead_size=%lu\n",
                (unsigned long)cfg->read_size, (unsigned long)cfg->prog_size,
                (unsigned long)cfg->cache_size, (unsigned long)cfg->lookahead_size);
        fprintf(logf, "CFG other: block_size=%lu, block_count=%lu, name_max=%lu, file_max=%lu, attr_max=%lu\n",
                (unsigned long)cfg->block_size, (unsigned long)cfg->block_count,
                (unsigned long)cfg->name_max, (unsigned long)cfg->file_max,
                (unsigned long)cfg->attr_max);
        fflush(logf);
    }

    err = lfs_format_(lfs, cfg);

    if (logf) {
        fprintf(logf, "lfs_format_: returned %d\n", err);

        /* Record state after calling internal formatter to help trace control flow.
           We again avoid touching user buffers; just snapshot pointers and sizes. */
        fprintf(logf, "POST state: lfs=%p, lfs->cfg=%p, lfs->pcache.buffer=%p (if present)\n",
                (void*)lfs, (void*)(lfs ? (void*)lfs->cfg : NULL),
                (void*)(lfs ? (void*)lfs->pcache.buffer : NULL));
        fflush(logf);
        fclose(logf);
    }

    LFS_TRACE("lfs_format -> %d", err);
    LFS_UNLOCK(cfg);
    return err;
}
