int lfs_format(lfs_t *lfs, const struct lfs_config *cfg) {
    int err = LFS_LOCK(cfg);

    /* Minimal on-entry probe to a persistent log for reproducer/forensics. */
    {
        FILE *___lfs_log = fopen("/tmp/logs/733.txt", "a");
        if (___lfs_log) {
            fprintf(___lfs_log, "ENTER lfs_format: lfs=%p cfg=%p\n", (void*)lfs, (void*)cfg);
            if (cfg) {
                fprintf(___lfs_log,
                        "  cfg->context=%p, read=%p, prog=%p, erase=%p, sync=%p\n",
                        (void*)cfg->context,
                        (void*)(uintptr_t)cfg->read,
                        (void*)(uintptr_t)cfg->prog,
                        (void*)(uintptr_t)cfg->erase,
                        (void*)(uintptr_t)cfg->sync);
                fprintf(___lfs_log,
                        "  read_size=%lu, prog_size=%lu, block_size=%lu, block_count=%lu\n",
                        (unsigned long)cfg->read_size,
                        (unsigned long)cfg->prog_size,
                        (unsigned long)cfg->block_size,
                        (unsigned long)cfg->block_count);
                fprintf(___lfs_log,
                        "  block_cycles=%ld, cache_size=%lu, lookahead_size=%lu\n",
                        (long)cfg->block_cycles,
                        (unsigned long)cfg->cache_size,
                        (unsigned long)cfg->lookahead_size);
                fprintf(___lfs_log,
                        "  read_buffer=%p, prog_buffer=%p, lookahead_buffer=%p\n",
                        (void*)cfg->read_buffer,
                        (void*)cfg->prog_buffer,
                        (void*)cfg->lookahead_buffer);
                fprintf(___lfs_log,
                        "  name_max=%lu, file_max=%lu, attr_max=%lu\n",
                        (unsigned long)cfg->name_max,
                        (unsigned long)cfg->file_max,
                        (unsigned long)cfg->attr_max);

                /* Heuristic/aid for reproducing the reported violation: explicitly
                   log the relationship that can lead to the overflow: whether a
                   cache-sized operation will be performed relative to the
                   user-supplied buffer pointer. We cannot know the supplied
                   buffer allocation size here, but we can show the configured
                   cache_size and the pointer that will be used for read cache.
                */
                fprintf(___lfs_log,
                        "  NOTE: cfg->cache_size=%lu; application-supplied cfg->read_buffer=%p\n",
                        (unsigned long)cfg->cache_size,
                        (void*)cfg->read_buffer);
            }
            fflush(___lfs_log);
            fclose(___lfs_log);
        }
    }

    if (err) {
        /* Log lock failure before returning so tests can observe path. */
        FILE *___lfs_log = fopen("/tmp/logs/733.txt", "a");
        if (___lfs_log) {
            fprintf(___lfs_log, "lfs_format: LFS_LOCK failed -> %d\n", err);
            fflush(___lfs_log);
            fclose(___lfs_log);
        }
        return err;
    }

    LFS_TRACE("lfs_format(%p, %p {.context=%p, "
                ".read=%p, .prog=%p, .erase=%p, .sync=%p, "
                ".read_size=%"PRIu32", .prog_size=%"PRIu32", "
                ".block_size=%"PRIu32", .block_count=%"PRIu32", "
                ".block_cycles=%"PRId32", .cache_size=%"PRIu32", "
                ".lookahead_size=%"PRIu32", .read_buffer=%p, "
                ".prog_buffer=%p, .lookahead_buffer=%p, "
                ".name_max=%"PRIu32", .file_max=%"PRIu32", "
                ".attr_max=%"PRIu32"}),",
            (void*)lfs, (void*)cfg, cfg->context,
            (void*)(uintptr_t)cfg->read, (void*)(uintptr_t)cfg->prog,
            (void*)(uintptr_t)cfg->erase, (void*)(uintptr_t)cfg->sync,
            cfg->read_size, cfg->prog_size, cfg->block_size, cfg->block_count,
            cfg->block_cycles, cfg->cache_size, cfg->lookahead_size,
            cfg->read_buffer, cfg->prog_buffer, cfg->lookahead_buffer,
            cfg->name_max, cfg->file_max, cfg->attr_max);

    /* Call through to the original implementation and log pre/post states. */
    {
        FILE *___lfs_log = fopen("/tmp/logs/733.txt", "a");
        if (___lfs_log) {
            fprintf(___lfs_log, "lfs_format: calling lfs_format_(lfs=%p, cfg=%p)\n", (void*)lfs, (void*)cfg);
            fflush(___lfs_log);
            fclose(___lfs_log);
        }
    }

    err = lfs_format_(lfs, cfg);

    /* Log the result and some state again to help correlate behavior in subsequent calls
       (for example, to show that cfg->read_buffer was non-NULL while cache_size was larger).
     */
    {
        FILE *___lfs_log = fopen("/tmp/logs/733.txt", "a");
        if (___lfs_log) {
            fprintf(___lfs_log, "lfs_format -> %d (lfs=%p cfg=%p)\n", err, (void*)lfs, (void*)cfg);
            if (cfg) {
                fprintf(___lfs_log, "  post: cache_size=%lu read_buffer=%p\n",
                        (unsigned long)cfg->cache_size, (void*)cfg->read_buffer);
            }
            fflush(___lfs_log);
            fclose(___lfs_log);
        }
    }

    LFS_TRACE("lfs_format -> %d", err);
    LFS_UNLOCK(cfg);
    return err;
}
