static int lfs_fs_deorphan(lfs_t *lfs, bool powerloss) {
    FILE *log = fopen("/tmp/logs/490.txt", "a");
    if (log) {
        fprintf(log, "ENTER lfs_fs_deorphan: lfs=%p powerloss=%d gorphans=%d\n",
                (void*)lfs, (int)powerloss, (int)lfs_gstate_hasorphans(&lfs->gstate));
        fflush(log);
    }

    int res = 0;

    if (!lfs_gstate_hasorphans(&lfs->gstate)) {
        if (log) {
            fprintf(log, "EXIT lfs_fs_deorphan: no orphans, returning 0\n");
            fclose(log);
        }
        return 0;
    }

    // Check for orphans in two separate passes:
    // - 1 for half-orphans (relocations)
    // - 2 for full-orphans (removes/renames)
    //
    // Two separate passes are needed as half-orphans can contain outdated
    // references to full-orphans, effectively hiding them from the deorphan
    // search.
    //
    int pass = 0;
    while (pass < 2) {
        if (log) {
            fprintf(log, "BEGIN pass %d\n", pass);
            fflush(log);
        }

        // Fix any orphans
        lfs_mdir_t pdir = {.split = true, .tail = {0, 1}};
        lfs_mdir_t dir;
        bool moreorphans = false;

        // iterate over all directory directory entries
        while (!lfs_pair_isnull(pdir.tail)) {
            if (log) {
                fprintf(log, "FETCH: pdir.tail={%llu, %llu} pdir.split=%d\n",
                        (unsigned long long)pdir.tail[0], (unsigned long long)pdir.tail[1], (int)pdir.split);
                fflush(log);
            }

            int err = lfs_dir_fetch(lfs, &dir, pdir.tail);
            if (err) {
                if (log) {
                    fprintf(log, "lfs_dir_fetch returned %d\n", err);
                    fclose(log);
                }
                return err;
            }

            // check head blocks for orphans
            if (!pdir.split) {
                // check if we have a parent
                lfs_mdir_t parent;
                lfs_stag_t tag = lfs_fs_parent(lfs, pdir.tail, &parent);
                if (tag < 0 && tag != LFS_ERR_NOENT) {
                    if (log) {
                        fprintf(log, "lfs_fs_parent error tag=%d\n", (int)tag);
                        fclose(log);
                    }
                    return tag;
                }

                if (pass == 0 && tag != LFS_ERR_NOENT) {
                    lfs_block_t pair[2];
                    lfs_stag_t state = lfs_dir_get(lfs, &parent,
                            LFS_MKTAG(0x7ff, 0x3ff, 0), tag, pair);
                    if (state < 0) {
                        if (log) {
                            fprintf(log, "lfs_dir_get failed state=%d\n", (int)state);
                            fclose(log);
                        }
                        return state;
                    }
                    lfs_pair_fromle32(pair);

                    if (!lfs_pair_issync(pair, pdir.tail)) {
                        // we have desynced
                        if (log) {
                            fprintf(log, "Desynced: pdir.tail={%llu, %llu} pair={%llu, %llu}\n",
                                    (unsigned long long)pdir.tail[0], (unsigned long long)pdir.tail[1],
                                    (unsigned long long)pair[0], (unsigned long long)pair[1]);
                            fflush(log);
                        }

                        // fix pending move in this pair? this looks like an
                        // optimization but is in fact _required_ since
                        // relocating may outdate the move.
                        uint16_t moveid = 0x3ff;
                        if (lfs_gstate_hasmovehere(&lfs->gstate, pdir.pair)) {
                            moveid = lfs_tag_id(lfs->gstate.tag);
                            if (log) {
                                fprintf(log, "Has move here: pdir.pair={%llu, %llu} moveid=0x%04x\n",
                                        (unsigned long long)pdir.pair[0], (unsigned long long)pdir.pair[1], moveid);
                                fflush(log);
                            }
                            lfs_fs_prepmove(lfs, 0x3ff, NULL);
                        }

                        lfs_pair_tole32(pair);
                        state = lfs_dir_orphaningcommit(lfs, &pdir, LFS_MKATTRS(
                                {LFS_MKTAG_IF(moveid != 0x3ff,
                                    LFS_TYPE_DELETE, moveid, 0), NULL},
                                {LFS_MKTAG(LFS_TYPE_SOFTTAIL, 0x3ff, 8),
                                    pair}));
                        lfs_pair_fromle32(pair);
                        if (state < 0) {
                            if (log) {
                                fprintf(log, "lfs_dir_orphaningcommit returned error %d\n", (int)state);
                                fclose(log);
                            }
                            return state;
                        }

                        if (log) {
                            fprintf(log, "lfs_dir_orphaningcommit returned state=%d\n", (int)state);
                            fflush(log);
                        }

                        // did our commit create more orphans?
                        if (state == LFS_OK_ORPHANED) {
                            moreorphans = true;
                            if (log) {
                                fprintf(log, "Created more orphans during half-orphan fix\n");
                                fflush(log);
                            }
                        }

                        // refetch tail
                        continue;
                    }
                }

                // note we only check for full orphans if we may have had a
                // power-loss, otherwise orphans are created intentionally
                // during operations such as lfs_mkdir
                if (pass == 1 && tag == LFS_ERR_NOENT && powerloss) {
                    // we are an orphan
                    if (log) {
                        fprintf(log, "Found full orphan: pdir.tail={%llu, %llu}\n",
                                (unsigned long long)pdir.tail[0], (unsigned long long)pdir.tail[1]);
                        fflush(log);
                    }

                    // steal state
                    err = lfs_dir_getgstate(lfs, &dir, &lfs->gdelta);
                    if (err) {
                        if (log) {
                            fprintf(log, "lfs_dir_getgstate returned %d\n", err);
                            fclose(log);
                        }
                        return err;
                    }

                    // steal tail
                    lfs_pair_tole32(dir.tail);
                    int state = lfs_dir_orphaningcommit(lfs, &pdir, LFS_MKATTRS(
                            {LFS_MKTAG(LFS_TYPE_TAIL + dir.split, 0x3ff, 8),
                                dir.tail}));
                    lfs_pair_fromle32(dir.tail);
                    if (state < 0) {
                        if (log) {
                            fprintf(log, "lfs_dir_orphaningcommit (full orphan) returned %d\n", state);
                            fclose(log);
                        }
                        return state;
                    }

                    if (log) {
                        fprintf(log, "lfs_dir_orphaningcommit (full) returned state=%d\n", state);
                        fflush(log);
                    }

                    // did our commit create more orphans?
                    if (state == LFS_OK_ORPHANED) {
                        moreorphans = true;
                        if (log) {
                            fprintf(log, "Created more orphans during full-orphan fix\n");
                            fflush(log);
                        }
                    }

                    // refetch tail
                    continue;
                }
            }

            pdir = dir;
        }

        pass = moreorphans ? 0 : pass+1;
        if (log) {
            fprintf(log, "END pass %d moreorphans=%d nextpass=%d\n", pass, (int)moreorphans, pass);
            fflush(log);
        }
    }

    // mark orphans as fixed
    res = lfs_fs_preporphans(lfs, -lfs_gstate_getorphans(&lfs->gstate));
    if (log) {
        fprintf(log, "Calling lfs_fs_preporphans, result=%d\n", res);
        fclose(log);
    }
    return res;
}
