static int lfs_fs_deorphan(lfs_t *lfs, bool powerloss) {
    FILE *log = fopen("/tmp/logs/490.txt", "a");
    if (log) {
        fprintf(log, "ENTER lfs_fs_deorphan: lfs=%p powerloss=%d\n", (void*)lfs, (int)powerloss);
        fflush(log);
    }

    int res = 0;
    if (!lfs_gstate_hasorphans(&lfs->gstate)) {
        if (log) {
            fprintf(log, "NO ORPHANS: exiting\n");
            fflush(log);
            fclose(log);
        }
        return 0;
    }

    // Check for orphans in two separate passes:
    // - 1 for half-orphans (relocations)
    // - 2 for full-orphans (removes/renames)
    //
    // Two separate passes are needed as half-orphans can contain outdated
    // references to full-orphans, effectively hiding them from the deorphan
    // search.
    //
    int pass = 0;
    while (pass < 2) {
        if (log) {
            fprintf(log, "PASS START: pass=%d\n", pass);
            fflush(log);
        }

        // Fix any orphans
        lfs_mdir_t pdir = {.split = true, .tail = {0, 1}};
        lfs_mdir_t dir;
        bool moreorphans = false;

        // iterate over all directory directory entries
        while (!lfs_pair_isnull(pdir.tail)) {
            if (log) {
                fprintf(log, "  ITER: pdir.tail={0x%08" PRIx32 ",0x%08" PRIx32 "} pdir.split=%d\n",
                        pdir.tail[0], pdir.tail[1], (int)pdir.split);
                fflush(log);
            }

            int err = lfs_dir_fetch(lfs, &dir, pdir.tail);
            if (err) {
                if (log) {
                    fprintf(log, "  ERROR: lfs_dir_fetch returned %d\n", err);
                    fflush(log);
                }
                res = err;
                goto cleanup;
            }

            // check head blocks for orphans
            if (!pdir.split) {
                // check if we have a parent
                lfs_mdir_t parent;
                lfs_stag_t tag = lfs_fs_parent(lfs, pdir.tail, &parent);
                if (log) {
                    fprintf(log, "  parent tag=%d\n", (int)tag);
                    fflush(log);
                }
                if (tag < 0 && tag != LFS_ERR_NOENT) {
                    if (log) {
                        fprintf(log, "  ERROR: lfs_fs_parent returned %d\n", (int)tag);
                        fflush(log);
                    }
                    res = (int)tag;
                    goto cleanup;
                }

                if (pass == 0 && tag != LFS_ERR_NOENT) {
                    lfs_block_t pair[2];
                    lfs_stag_t state = lfs_dir_get(lfs, &parent,
                            LFS_MKTAG(0x7ff, 0x3ff, 0), tag, pair);
                    if (state < 0) {
                        if (log) {
                            fprintf(log, "  ERROR: lfs_dir_get returned %d\n", (int)state);
                            fflush(log);
                        }
                        res = (int)state;
                        goto cleanup;
                    }
                    lfs_pair_fromle32(pair);

                    if (!lfs_pair_issync(pair, pdir.tail)) {
                        // we have desynced
                        if (log) {
                            fprintf(log, "  DESYNC: pdir.tail={0x%08" PRIx32 ",0x%08" PRIx32 "} -> pair={0x%08" PRIx32 ",0x%08" PRIx32 "}\n",
                                    pdir.tail[0], pdir.tail[1], pair[0], pair[1]);
                            fflush(log);
                        }

                        // fix pending move in this pair? this looks like an
                        // optimization but is in fact _required_ since
                        // relocating may outdate the move.
                        uint16_t moveid = 0x3ff;
                        if (lfs_gstate_hasmovehere(&lfs->gstate, pdir.pair)) {
                            moveid = lfs_tag_id(lfs->gstate.tag);
                            if (log) {
                                fprintf(log, "  Fixing move while fixing orphans pdir.pair={0x%08" PRIx32 ",0x%08" PRIx32 "} moveid=0x%03" PRIx16 "\n",
                                        pdir.pair[0], pdir.pair[1], moveid);
                                fflush(log);
                            }
                            lfs_fs_prepmove(lfs, 0x3ff, NULL);
                        }

                        lfs_pair_tole32(pair);
                        state = lfs_dir_orphaningcommit(lfs, &pdir, LFS_MKATTRS(
                                {LFS_MKTAG_IF(moveid != 0x3ff,
                                    LFS_TYPE_DELETE, moveid, 0), NULL},
                                {LFS_MKTAG(LFS_TYPE_SOFTTAIL, 0x3ff, 8),
                                    pair}));
                        lfs_pair_fromle32(pair);
                        if (log) {
                            fprintf(log, "  Called lfs_dir_orphaningcommit -> state=%d\n", (int)state);
                            fflush(log);
                        }
                        if (state < 0) {
                            if (log) {
                                fprintf(log, "  ERROR: lfs_dir_orphaningcommit returned %d\n", (int)state);
                                fflush(log);
                            }
                            res = (int)state;
                            goto cleanup;
                        }

                        // did our commit create more orphans?
                        if (state == LFS_OK_ORPHANED) {
                            moreorphans = true;
                            if (log) {
                                fprintf(log, "  lfs_dir_orphaningcommit reported LFS_OK_ORPHANED\n");
                                fflush(log);
                            }
                        }

                        // refetch tail
                        if (log) {
                            fprintf(log, "  REFETCH: continuing to refetch tail after orphaningcommit\n");
                            fflush(log);
                        }
                        continue;
                    }
                }

                // note we only check for full orphans if we may have had a
                // power-loss, otherwise orphans are created intentionally
                // during operations such as lfs_mkdir
                if (pass == 1 && tag == LFS_ERR_NOENT && powerloss) {
                    // we are an orphan
                    if (log) {
                        fprintf(log, "  FULL ORPHAN detected for pdir.tail={0x%08" PRIx32 ",0x%08" PRIx32 "}\n",
                                pdir.tail[0], pdir.tail[1]);
                        fflush(log);
                    }

                    // steal state
                    err = lfs_dir_getgstate(lfs, &dir, &lfs->gdelta);
                    if (err) {
                        if (log) {
                            fprintf(log, "  ERROR: lfs_dir_getgstate returned %d\n", err);
                            fflush(log);
                        }
                        res = err;
                        goto cleanup;
                    }

                    // steal tail
                    lfs_pair_tole32(dir.tail);
                    int state = lfs_dir_orphaningcommit(lfs, &pdir, LFS_MKATTRS(
                            {LFS_MKTAG(LFS_TYPE_TAIL + dir.split, 0x3ff, 8),
                                dir.tail}));
                    lfs_pair_fromle32(dir.tail);
                    if (log) {
                        fprintf(log, "  Called lfs_dir_orphaningcommit (steal tail) -> state=%d\n", state);
                        fflush(log);
                    }
                    if (state < 0) {
                        if (log) {
                            fprintf(log, "  ERROR: lfs_dir_orphaningcommit (steal tail) returned %d\n", state);
                            fflush(log);
                        }
                        res = state;
                        goto cleanup;
                    }

                    // did our commit create more orphans?
                    if (state == LFS_OK_ORPHANED) {
                        moreorphans = true;
                        if (log) {
                            fprintf(log, "  lfs_dir_orphaningcommit (steal tail) reported LFS_OK_ORPHANED\n");
                            fflush(log);
                        }
                    }

                    // refetch tail
                    if (log) {
                        fprintf(log, "  REFETCH: continuing to refetch tail after steal-tail orphaningcommit\n");
                        fflush(log);
                    }
                    continue;
                }
            }

            pdir = dir;
        }

        pass = moreorphans ? 0 : pass+1;
        if (log) {
            fprintf(log, "PASS END: pass=%d moreorphans=%d next_pass=%d\n", (int)pass, (int)moreorphans, pass);
            fflush(log);
        }
    }

    // mark orphans as fixed
    res = lfs_fs_preporphans(lfs, -lfs_gstate_getorphans(&lfs->gstate));
    if (log) {
        fprintf(log, "FINAL: lfs_fs_preporphans returned %d\n", res);
        fflush(log);
    }

cleanup:
    if (log) {
        fprintf(log, "EXIT lfs_fs_deorphan: res=%d\n", res);
        fflush(log);
        fclose(log);
    }
    return res;
}
