int lfs_fs_grow(lfs_t *lfs, lfs_size_t block_count) {
    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        /* Log lock failure and input state */
        FILE *f = fopen("/tmp/logs/32.txt", "a");
        if (f) {
            fprintf(f, "lfs_fs_grow ENTRY (lock failed): lfs=%p, block_count=%"PRIu32", &block_count=%p, err=%d\n",
                    (void*)lfs, block_count, (void*)&block_count, err);
            fclose(f);
        }
        return err;
    }

    /* Log entry and initial state of the parameter and its address */
    FILE *f = fopen("/tmp/logs/32.txt", "a");
    if (f) {
        fprintf(f, "lfs_fs_grow ENTRY: lfs=%p, block_count=%"PRIu32", &block_count=%p\n",
                (void*)lfs, block_count, (void*)&block_count);
        fclose(f);
    }

    LFS_TRACE("lfs_fs_grow(%p, %"PRIu32")", (void*)lfs, block_count);

    /* Call the original implementation (preserve behavior) */
    err = lfs_fs_grow_(lfs, block_count);

    /* Log return value and parameter state after the call to detect any indirect modification */
    FILE *f2 = fopen("/tmp/logs/32.txt", "a");
    if (f2) {
        fprintf(f2, "lfs_fs_grow AFTER lfs_fs_grow_: ret=%d, block_count=%"PRIu32", &block_count=%p\n",
                err, block_count, (void*)&block_count);
        fclose(f2);
    }

    LFS_TRACE("lfs_fs_grow -> %d", err);
    LFS_UNLOCK(lfs->cfg);
    return err;
}
