int lfs_fs_grow(lfs_t *lfs, lfs_size_t block_count) {
    /* Log entry and the cfg expression value so that the evaluation of
       the argument to LFS_LOCK can be observed. We append to the log file
       so successive runs accumulate evidence. */
    FILE *log = fopen("/tmp/logs/38.txt", "a");
    if (log) {
        fprintf(log, "[enter] lfs_fs_grow(lfs=%p, block_count=%" PRIu32 ")\n", (void*)lfs, block_count);
        if (lfs) {
            /* Attempt to record the value of lfs->cfg as a pointer. In the
               common littlefs layout cfg is a pointer type; if not, this
               will still attempt to cast to void* for a readable log. */
            fprintf(log, "[state] before LFS_LOCK: lfs->cfg=(%p)\n", (void*)(lfs->cfg));
        } else {
            fprintf(log, "[state] before LFS_LOCK: lfs is NULL\n");
        }
        fclose(log);
    }

    /* Preserve original behaviour: call the lock macro exactly as before. */
    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        FILE *log2 = fopen("/tmp/logs/38.txt", "a");
        if (log2) {
            fprintf(log2, "[early return] LFS_LOCK returned %d\n", err);
            if (lfs) {
                fprintf(log2, "[state] after LFS_LOCK (early): lfs->cfg=(%p)\n", (void*)(lfs->cfg));
            }
            fclose(log2);
        }
        return err;
    }

    LFS_TRACE("lfs_fs_grow(%p, %"PRIu32")", (void*)lfs, block_count);

    /* Log before delegating to the internal grow function */
    FILE *log3 = fopen("/tmp/logs/38.txt", "a");
    if (log3) {
        fprintf(log3, "[call] calling lfs_fs_grow_(lfs=%p, block_count=%" PRIu32 ")\n", (void*)lfs, block_count);
        if (lfs) {
            fprintf(log3, "[state] just before lfs_fs_grow_: lfs->cfg=(%p)\n", (void*)(lfs->cfg));
        }
        fclose(log3);
    }

    err = lfs_fs_grow_(lfs, block_count);

    /* Log result of internal call */
    FILE *log4 = fopen("/tmp/logs/38.txt", "a");
    if (log4) {
        fprintf(log4, "[result] lfs_fs_grow_ -> %d\n", err);
        if (lfs) {
            fprintf(log4, "[state] after lfs_fs_grow_: lfs->cfg=(%p)\n", (void*)(lfs->cfg));
        }
        fclose(log4);
    }

    LFS_TRACE("lfs_fs_grow -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    /* Final log showing state after unlock and return value */
    FILE *log5 = fopen("/tmp/logs/38.txt", "a");
    if (log5) {
        fprintf(log5, "[exit] lfs_fs_grow returning %d\n", err);
        if (lfs) {
            fprintf(log5, "[state] after LFS_UNLOCK: lfs->cfg=(%p)\n", (void*)(lfs->cfg));
        }
        fclose(log5);
    }

    return err;
}
