int lfs_fs_grow(lfs_t *lfs, lfs_size_t block_count) {
    int err = LFS_LOCK(lfs->cfg);

    /* Open a log file to record control flow and state for debugging the
       potential dead-code scenario described (LFS_UNLOCK being a no-op). */
    FILE *log = fopen("/tmp/logs/38.txt", "a");
    if (log) {
        /* Log entry: function entered, parameters and lock result. */
        fprintf(log, "lfs_fs_grow ENTER: lfs=%p block_count=%" PRIu32 " lock_err=%d cfg=%p\n",
                (void*)lfs, block_count, err, (void*)lfs->cfg);
        fflush(log);
    }

    if (err) {
        if (log) {
            fprintf(log, "lfs_fs_grow EXIT early: err=%d (lock failure)\n", err);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_fs_grow(%p, %"PRIu32")", (void*)lfs, block_count);

    err = lfs_fs_grow_(lfs, block_count);

    if (log) {
        fprintf(log, "lfs_fs_grow after lfs_fs_grow_: err=%d\n", err);
        fflush(log);
    }

    LFS_TRACE("lfs_fs_grow -> %d", err);

    if (log) {
        /* Record that we are about to call LFS_UNLOCK. In configurations where
           LFS_UNLOCK expands to ((void)cfg), this call will be a no-op. Logging
           it here provides evidence that the call was reached and executed. */
        fprintf(log, "lfs_fs_grow: calling LFS_UNLOCK(cfg=%p)\n", (void*)lfs->cfg);
        fflush(log);
    }

    LFS_UNLOCK(lfs->cfg);

    if (log) {
        fprintf(log, "lfs_fs_grow: returned from LFS_UNLOCK; returning err=%d\n", err);
        fclose(log);
    }

    return err;
}
