int lfs_fs_grow(lfs_t *lfs, lfs_size_t block_count) {
    /* Instrumentation/logging to capture control flow and state for the
       LFS_LOCK result and err path. Writes appended logs to /tmp/logs/38.txt.
       Preserve original semantics: call LFS_LOCK exactly once and behave the
       same based on its return value. */
    int err;

    /* Attempt to open log file for append. If this fails, fall back to
       stderr for at least some observable output. */
    FILE *log = fopen("/tmp/logs/38.txt", "a");
    if (log) {
        fprintf(log, "ENTER lfs_fs_grow: lfs=%p block_count=%llu\n", (void*)lfs,
                (unsigned long long)block_count);
    }

    /* Call LFS_LOCK exactly once and record its return value. This preserves
       the original control flow (which assigned the result to err and then
       checked it). Logging the value demonstrates the reported violation if
       the value is always 0. */
    int lock_res = LFS_LOCK(lfs->cfg);
    if (log) {
        fprintf(log, "LFS_LOCK(lfs->cfg) returned %d\n", lock_res);
    } else {
        /* Provide at least some trace if file logging isn't available. */
        fprintf(stderr, "lfs_fs_grow: LFS_LOCK(lfs->cfg) returned %d\n", lock_res);
    }

    err = lock_res;
    if (log) {
        fprintf(log, "err assigned %d\n", err);
    }

    if (err) {
        if (log) {
            fprintf(log, "err non-zero -> early return %d\n", err);
            fclose(log);
        }
        return err;
    }

    /* Preserve original trace call and core operation. */
    LFS_TRACE("lfs_fs_grow(%p, %"PRIu32")", (void*)lfs, block_count);

    if (log) {
        fprintf(log, "Calling lfs_fs_grow_(...)\n");
    }
    err = lfs_fs_grow_(lfs, block_count);
    if (log) {
        fprintf(log, "lfs_fs_grow_ returned %d\n", err);
    }

    LFS_TRACE("lfs_fs_grow -> %d", err);

    if (log) {
        fprintf(log, "Calling LFS_UNLOCK(lfs->cfg)\n");
    }
    LFS_UNLOCK(lfs->cfg);

    if (log) {
        fprintf(log, "Returning %d\n", err);
        fclose(log);
    }

    return err;
}
