int lfs_fs_grow(lfs_t *lfs, lfs_size_t block_count) {
    /* include stdio here so we can write logs to the filesystem without
       requiring changes to global includes. This inclusion is local to the
       function (preprocessed into the function body) and provides fopen/fprintf/fclose. */
    #include <stdio.h>

    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        /* Log the early return (lock failed) so the control path is visible */
        FILE *log = fopen("/tmp/logs/38.txt", "a");
        if (log) {
            fprintf(log, "lfs_fs_grow: entered, LFS_LOCK returned %d, lfs=%p, cfg=%p, block_count=%llu\n",
                err, (void*)lfs, (void*)lfs->cfg, (unsigned long long)block_count);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_fs_grow(%p, %"PRIu32")", (void*)lfs, block_count);

    err = lfs_fs_grow_(lfs, block_count);

    LFS_TRACE("lfs_fs_grow -> %d", err);

    /* Log state immediately before the unlock so we can observe the potential
       dead-code scenario: LFS_UNLOCK may be a no-op macro like ((void)cfg). */
    {
        FILE *log = fopen("/tmp/logs/38.txt", "a");
        if (log) {
            fprintf(log, "lfs_fs_grow: before LFS_UNLOCK, err=%d, lfs=%p, cfg=%p\n",
                err, (void*)lfs, (void*)lfs->cfg);
            fclose(log);
        }
    }

    LFS_UNLOCK(lfs->cfg);

    /* Log state after the unlock to demonstrate whether any observable
       change occurred as a result of calling LFS_UNLOCK. If LFS_UNLOCK is
       ((void)cfg) this will show identical pre/post state, providing
       evidence of dead code. */
    {
        FILE *log = fopen("/tmp/logs/38.txt", "a");
        if (log) {
            fprintf(log, "lfs_fs_grow: after LFS_UNLOCK, err=%d, lfs=%p, cfg=%p\n",
                err, (void*)lfs, (void*)lfs->cfg);
            fclose(log);
        }
    }

    return err;
}
