int lfs_fs_grow(lfs_t *lfs, lfs_size_t block_count) {
    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        /* Log lock failure so tests can see early return path */
        FILE *f = fopen("/tmp/logs/35.txt", "a");
        if (f) {
            fprintf(f, "lfs_fs_grow: LFS_LOCK failed for lfs=%p err=%d\n", (void*)lfs, err);
            fclose(f);
        }
        return err;
    }

    LFS_TRACE("lfs_fs_grow(%p, %"PRIu32")", (void*)lfs, block_count);

    /* Probe: log entry, relevant pointers and a small read of the lookahead buffer.
       This helps demonstrate whether lookahead.buffer is only read (supporting the
       claim that it could be declared const uint8_t *). */
    {
        FILE *f = fopen("/tmp/logs/35.txt", "a");
        if (f) {
            fprintf(f, "ENTER lfs_fs_grow: lfs=%p block_count=%"PRIu32"\n",
                    (void*)lfs, (uint32_t)block_count);
            fprintf(f, "cfg=%p\n", (void*)lfs->cfg);

            /* Attempt to observe lookahead buffer pointer and size. These members
               exist in the target codebase (lfs_t::lookahead.buffer and
               lfs_t::lookahead.size). Guard reads to avoid crashes. */
            void *lookbuf = NULL;
            size_t looksize = 0;
            /* Accessing these members directly to capture their runtime values. */
            lookbuf = (void*)lfs->lookahead.buffer;
            looksize = (size_t)lfs->lookahead.size;

            fprintf(f, "lookahead.buffer=%p lookahead.size=%zu\n", lookbuf, looksize);

            if (lookbuf != NULL && looksize > 0) {
                size_t tolog = looksize < 16 ? looksize : 16;
                uint8_t *b = (uint8_t*)lookbuf;
                fprintf(f, "lookahead[0..%zu):", tolog);
                for (size_t i = 0; i < tolog; i++) {
                    /* Read-only access: print bytes as hex. This demonstrates non-mutating
                       use of the buffer (evidence that it could be const-qualified). */
                    fprintf(f, " %02x", b[i]);
                }
                fprintf(f, "\n");
            }

            fclose(f);
        }
    }

    err = lfs_fs_grow_(lfs, block_count);

    LFS_TRACE("lfs_fs_grow -> %d", err);

    /* Log exit and result */
    {
        FILE *f = fopen("/tmp/logs/35.txt", "a");
        if (f) {
            fprintf(f, "EXIT lfs_fs_grow: err=%d\n", err);
            fclose(f);
        }
    }

    LFS_UNLOCK(lfs->cfg);
    return err;
}
