int lfs_fs_mkconsistent(lfs_t *lfs) {
    /* Probe logging to capture control flow and state for MISRA Rule 14.3 investigation. */
    int err = LFS_LOCK(lfs->cfg);

    /* Attempt to append diagnostic logs to /tmp/logs/409.txt. Do not change control flow. */
    FILE *__logf = NULL;
    __logf = fopen("/tmp/logs/409.txt", "a");
    if (__logf) {
        fprintf(__logf, "lfs_fs_mkconsistent ENTER: lfs=%p, cfg=%p, lock_ret=%d\n",
                (void*)lfs, (void*)(lfs ? lfs->cfg : NULL), err);
        fflush(__logf);
    }

    if (err) {
        if (__logf) {
            fprintf(__logf, "lfs_fs_mkconsistent EARLY RETURN: lock failed, err=%d\n", err);
            fclose(__logf);
        }
        return err;
    }

    if (__logf) {
        fprintf(__logf, "lfs_fs_mkconsistent: lock succeeded (lock_ret=%d). Proceeding to lfs_fs_mkconsistent_.\n", err);
        fflush(__logf);
    }

    LFS_TRACE("lfs_fs_mkconsistent(%p)", (void*)lfs);

    err = lfs_fs_mkconsistent_(lfs);

    if (__logf) {
        fprintf(__logf, "lfs_fs_mkconsistent: returned from lfs_fs_mkconsistent_ with err=%d\n", err);
    }

    LFS_TRACE("lfs_fs_mkconsistent -> %d", err);

    LFS_UNLOCK(lfs->cfg);

    if (__logf) {
        fprintf(__logf, "lfs_fs_mkconsistent: unlocked cfg=%p, final err=%d\n", (void*)lfs->cfg, err);
        fclose(__logf);
    }

    return err;
}
